import Bentogrid from "@/components/shadcn-space/blocks/bento-grid-01/bentogrid";

const Page = () => {
  return <Bentogrid />;
};

export default Page;

"use client";
import { motion } from "motion/react";
import { AppWindowMac, Box, ChartColumnBig, Command, Presentation, ShieldCheck, WandSparkles } from "lucide-react";

export default function AnimatedUiBlock() {
    return (
        <>
            <div className="min-h-[216px] flex items-center justify-center relative">
                <span className="flex items-center justify-center rounded-full shadow-lg">
                    <img alt="shadcn dark logo" src="https://images.shadcnspace.com/assets/logo/shadcn-logo.png" width={96} height={96} />
                </span>
                <motion.div
                    className="absolute start-[70%] top-0 z-10"
                    animate={{
                        y: [-15, 15, -15]
                    }}
                    transition={{
                        duration: 3,
                        ease: "easeInOut",
                        repeat: Infinity
                    }}>
                    <span aria-label="Multiple Layouts" className="lg:size-18 size-11 flex items-center justify-center rounded-full bg-background">
                        <AppWindowMac size={32} />
                    </span>
                </motion.div>
                <motion.div
                    className="absolute start-[91%] top-[28%] z-10"
                    animate={{
                        y: [15, -15, 15]
                    }}
                    transition={{
                        duration: 3,
                        ease: "easeInOut",
                        repeat: Infinity,
                    }}>
                    <span aria-label="UI Blocks" className="lg:size-12 size-7 flex items-center justify-center rounded-full bg-background">
                        <Command size={24} />
                    </span>
                </motion.div>
                <motion.div
                    className="absolute start-[78%] top-[61%] z-10"
                    animate={{
                        y: [-15, 15, -15]
                    }}
                    transition={{
                        duration: 3,
                        ease: "easeInOut",
                        repeat: Infinity,
                        delay: 1
                    }}>
                    <span aria-label="Authentication" className="lg:size-18 size-11 flex items-center justify-center rounded-full bg-background">
                        <ShieldCheck size={36} />
                    </span>
                </motion.div>
                <motion.div
                    className="absolute end-[78%] top-0"
                    animate={{
                        y: [15, -15, 15]
                    }}
                    transition={{
                        duration: 3,
                        ease: "easeInOut",
                        repeat: Infinity,
                    }}>
                    <span aria-label="Animated Components" className="lg:size-12 size-7 flex items-center justify-center rounded-full bg-background">
                        <Presentation size={20} />
                    </span>
                </motion.div>
                <motion.div
                    className="absolute end-[67%] lg:top-[61%] top-[80%] z-10"
                    animate={{
                        y: [-15, 15, -15]
                    }}
                    transition={{
                        duration: 3,
                        ease: "easeInOut",
                        repeat: Infinity,
                        delay: 0.2
                    }}>
                    <span aria-label="Ai Builder" className="lg:size-18 size-11 flex items-center justify-center rounded-full bg-background">
                        <WandSparkles size={32} />
                    </span>
                </motion.div>
                <motion.div
                    className="absolute lg:end-[98%] end-[80%] lg:top-0 top-[30%]"
                    animate={{
                        y: [15, -15, 15]
                    }}
                    transition={{
                        duration: 3,
                        ease: "easeInOut",
                        repeat: Infinity,
                        delay: 0.5
                    }}>
                    <span aria-label="Charts" className="lg:size-18 size-11 flex items-center justify-center rounded-full bg-background">
                        <ChartColumnBig size={32} />
                    </span>
                </motion.div>
                <motion.div
                    className="absolute end-[95%] top-[67%] z-10"
                    animate={{
                        y: [-15, 15, -15]
                    }}
                    transition={{
                        duration: 3,
                        ease: "easeInOut",
                        repeat: Infinity,
                        delay: 0.7
                    }}>
                    <span aria-label="UI Components" className="lg:size-12 size-7 flex items-center justify-center rounded-full bg-background">
                        <Box size={22} />
                    </span>
                </motion.div>
            </div >
        </>
    )
}

"use client";
import { BarChart3, ChartArea, Check, CirclePlay, Diamond, FormInput, LayoutGrid, LucideIcon, Route } from "lucide-react";
import { motion, AnimatePresence } from "motion/react";
import { useEffect, useState } from "react";

type Item = {
  id: string;
  title: string;
  icon: LucideIcon;
};

const DATA: Item[] = [
  {
    id: "1",
    title: "Breadcrumb component",
    icon: Route,
  },
  {
    id: "2",
    title: "Animated component",
    icon: CirclePlay,
  },
  {
    id: "3",
    title: "UI components",
    icon: Diamond,
  },
  {
    id: "4",
    title: "Form components",
    icon: FormInput,
  },
  {
    id: "5",
    title: "Chart components",
    icon: BarChart3,
  },
  {
    id: "6",
    title: "Layout components",
    icon: LayoutGrid,
  },
  {
    id: "7",
    title: "Area Chart",
    icon: ChartArea,
  },
];

export default function ReminderCarousel() {
  const [visible, setVisible] = useState<Item[]>(DATA.slice(0, 3));
  const [pointer, setPointer] = useState(2);

  useEffect(() => {
    const interval = setInterval(() => {
      setVisible((prev) => {
        const nextIndex = (pointer + 1) % DATA.length;
        setPointer(nextIndex);
        return [...prev.slice(1), DATA[nextIndex]];
      });
    }, 2000);

    return () => clearInterval(interval);
  }, [pointer]);

  return (
    <div className="relative flex flex-col items-center rounded-2xl h-52 w-full overflow-hidden">
      <AnimatePresence initial={false}>
        {visible.map((item, i) => (
          <motion.div
            key={item.id}
            initial={{ y: 215, opacity: 0, scale: 0.9 }}
            animate={{
              y: i * 70,
              scale: i === 1 ? 1 : 0.9,
              opacity: i === 1 ? 1 : 0.5,
            }}
            exit={{ y: -100, opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.6, ease: "easeInOut" }}
            className="absolute flex items-center justify-between w-full px-5 py-4 rounded-xl bg-background border border-border text-card-foreground">
            <div className="flex items-center justify-between w-full">
              <div className="flex items-center gap-2">
                <item.icon size={20}/>
                <span className="text-sm font-medium">{item.title}</span>
              </div>
              <Check size={20}/>
            </div>
          </motion.div>
        ))}
      </AnimatePresence>
    </div>
  );
}
