import React from "react";
import { Icon } from "@iconify/react";
import { cn } from "@/lib/utils";
import '@/components/shadcn-space/circular-icons/circular-icons.css'

export interface CircularIconsProps extends React.HTMLAttributes<HTMLDivElement> {
  className?: string;
  children?: React.ReactNode;
  reverse?: boolean;
  duration?: number;
  delay?: number;
  radius?: number;
  path?: boolean;
  iconSize?: number;
  speed?: number;
}

export function CircularIcons({
  className,
  children,
  reverse,
  duration = 20,
  radius = 160,
  path = true,
  speed = 1,
  ...props
}: CircularIconsProps) {
  const calculatedDuration = duration / speed;
  return (
    <>
      {path && (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          version="1.1"
          className="pointer-events-none absolute inset-0 size-full"
        >
          <circle
            className="stroke-black/10 stroke-1 dark:stroke-white/10"
            cx="50%"
            cy="50%"
            r={radius}
            fill="none"
          />
        </svg>
      )}
      {React.Children.map(children, (child, index) => {
        const angle = (360 / React.Children.count(children)) * index;
        return (
          <div
            style={
              {
                "--duration": `${calculatedDuration}s`,
                "--radius": `${radius}px`,
                "--angle": `${angle}deg`,
              } as React.CSSProperties
            }
            className={cn("animate-orbit", reverse && "reverse", className)}
            {...props}
          >
            {child}
          </div>
        );
      })}
    </>
  );
}

export default function CircularIconsDemo() {
  return (
    <>
      <div className="flex items-center justify-center w-full">
        <div className="relative flex h-[500px] w-full items-center justify-center overflow-hidden">
          <CircularIcons>
            <Icon
              icon="solar:sun-bold"
              width={20}
              height={20}
              className="w-full h-full text-foreground"
            />
            <Icon
              icon="solar:planet-bold"
              width={20}
              height={20}
              className="w-full h-full"
            />
            <Icon
              icon="solar:star-bold"
              width={20}
              height={20}
              className="w-full h-full"
            />
            <Icon
              icon="solar:moon-bold"
              width={20}
              height={20}
              className="w-full h-full"
            />
            <Icon
              icon="solar:sun-bold"
              width={20}
              height={20}
              className="w-full h-full"
            />
          </CircularIcons>

          <CircularIcons radius={100} reverse speed={2}>
            <Icon
              icon="solar:planet-linear"
              width={20}
              height={20}
              className="w-full h-full"
            />
            <Icon
              icon="solar:star-linear"
              width={20}
              height={20}
              className="w-full h-full"
            />
            <Icon
              icon="solar:moon-linear"
              width={20}
              height={20}
              className="w-full h-full"
            />
            <Icon
              icon="solar:sun-linear"
              width={20}
              height={20}
              className="w-full h-full"
            />
          </CircularIcons>
        </div>
      </div>
    </>
  );
}

@keyframes orbit {
  0% {
    transform: rotate(var(--angle, 0deg)) translateY(var(--radius, 50px))
      rotate(calc(var(--angle, 0deg) * -1));
  }
  100% {
    transform: rotate(calc(var(--angle, 0deg) + 360deg))
      translateY(var(--radius, 50px))
      rotate(calc(var(--angle, 0deg) * -1 - 360deg));
  }
}

/* Orbit animation class */
.animate-orbit {
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  transform-origin: center center;
  width: var(--icon-size, 30px);
  height: var(--icon-size, 30px);
  animation: orbit var(--duration, 20s) linear infinite;
}

/* Optional reverse animation */
.animate-orbit.reverse {
  animation-direction: reverse;
}
