                    <div className={`flex flex-col items-center gap-3 sm:py-0 sm:px-0 py-5 px-8 sm:border-0 border-gray-200 dark:border-gray-700 w-full ${item.className}`}>
                      {item.icon ? (
                        <>
                          <item.icon size={28} className='text-foreground font-light' />
                          <p className='text-sm font-normal text-muted-foreground'>
                            {item.label}
                          </p>
                        </>
                      ) : (
                        <>
                          <p className='sm:text-xl text-lg font-semibold text-foreground'>
                            {item.price}
                          </p>
                          <p className='text-sm font-normal text-muted-foreground'>
                            {item.label}
                          </p>
                        </>
                      )}
                    </div>
                    {index < propertyFeatures.length - 1 && (
                      <Separator orientation="vertical" className="h-12 my-auto sm:block hidden" />
                    )}
                  </motion.div>
                ))}
              </motion.div>
            </div>
          </div>
        </div>
        {/* Clouds */}
        <>
          <SeamlessCloud
            cloudCount={2}
            minSize={400}
            maxSize={678}
            opacity="opacity-60"
            gapMin={100}
            gapMax={500}
            top="top-56 sm:top-40 left-0"
          />
        </>
      </div>
    </section>
  );
};

export default HeroSection;

'use client';
import { Marquee } from '@/components/shadcn-space/animations/marquee';
import { useEffect, useState } from 'react';

export default function SeamlessCloud({
  top = "top-72 sm:top-20",
  cloudCount = 5,
  minSize = 400,
  maxSize = 678,
  opacity = "opacity-60",
  gapMin = 50,
  gapMax = 200,
}) {
  const [clouds, setClouds] = useState<{ width: number; gap: number }[]>([]);

  useEffect(() => {
    const generated = Array.from({ length: cloudCount }).map(() => ({
      width: Math.floor(Math.random() * (maxSize - minSize + 1)) + minSize,
      gap: Math.floor(Math.random() * (gapMax - gapMin + 1)) + gapMin,
    }));

    setClouds(generated);
  }, [cloudCount, minSize, maxSize, gapMin, gapMax]);

  if (clouds.length === 0) return null; // prevents mismatch

  return (
    <div
      className={`absolute ${top} inset-x-0 z-0 overflow-hidden pointer-events-none`}
    >
      <Marquee pauseOnHover className="[--duration:30s]">
        {clouds.map((cloud, i) => (
          <img
            key={i}
            src="https://images.shadcnspace.com/assets/backgrounds/cloud.webp"
            alt="cloud"
            width={cloud.width}
            height={350}
            className={opacity}
            style={{ marginRight: cloud.gap }}
          />
        ))}
      </Marquee>
    </div>
  );
}

"use client";

import React, { useEffect, useState, useCallback, useRef } from 'react'
import { cn } from '@/lib/utils';
import {
  Sheet,
  SheetContent,
  SheetClose,
  SheetHeader,
} from '@/components/ui/sheet'
import { Button } from '@/components/ui/button'
import { Menu, Phone, X } from 'lucide-react'
import { Separator } from '@/components/ui/separator';
import { motion, useInView } from "motion/react";

type navData = {
  name: string;
  href: string;
};

type NavbarProps = {
  navData: navData[]
}

export type NavLinkItem = {
  name: string
  label?: string
  href: string
  isActive?: boolean
}

export interface NavLinkProps {
  item: NavLinkItem
}

const NavLink: React.FC<NavLinkProps> = ({
  item,
}) => {

  return (
    <li className="flex items-center group w-fit cursor-pointer">
      <div className={`h-0.5 bg-primary transition-all duration-300 ${item.isActive ? 'w-6 mr-4' : 'w-0 group-hover:w-6 group-hover:mr-4'}`} />
      <a
        href={item.href}
        className={`text-2xl font-medium rounded-full transition-colors ${item.isActive ? 'text-primary' : "text-muted-foreground group-hover:text-primary"}`}
      >
        {item.name}
      </a>
    </li>
  )
}

const Navbar: React.FC<NavbarProps> = ({ navData }) => {
  const [sticky, setSticky] = useState(false);
  const [navbarOpen, setNavbarOpen] = useState(false);
  const headerRef = useRef<HTMLElement>(null);
  const isInView = useInView(headerRef, { once: true, amount: 0.1 });

  const handleScroll = useCallback(() => {
    setSticky(window.scrollY >= 50);
  }, []);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [handleScroll]);

  return (
    <motion.header
      ref={headerRef}
      initial={{ opacity: 0, y: -32 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: -32 }}
      transition={{ duration: 0.7, ease: "easeInOut" }}
      className={cn(
        "z-50 w-full bg-transparent h-20 sticky top-0 flex items-center"
      )}
    >
      <nav
        className={`max-w-7xl mx-auto sm:px-16 px-4 w-full`}>
        <div className={`flex items-center justify-between duration-300 ${sticky
          ? 'shadow-lg bg-background rounded-full p-3'
          : 'shadow-none px-0'
          }`}>
          <div className='flex justify-between items-center gap-2 w-full'>
            <div>
              <a href='#'>
                <img
                  src={
                    'https://images.shadcnspace.com/assets/logo/shadcnspace-logo-black.svg'
                  }
                  alt='logo'
                  width={150}
                  height={68}
                  className={`${sticky ? 'block dark:hidden' : 'hidden'}`}
                />
                <img
                  src={
                    'https://images.shadcnspace.com/assets/logo/shadcnspace-logo-white.svg'
                  }
                  alt='logo'
                  width={150}
                  height={68}
                  className={`${sticky ? 'hidden dark:block' : 'block'}`}
                />
              </a>
            </div>

            <div className='flex items-center gap-2 sm:gap-6'>
              <div className={`hidden md:block`}>
                <a
                  href='tel:+1212456789'
                  className={`text-sm text-inherit flex items-center gap-2 ${sticky
                    ? 'text-foreground hover:text-primary border-white/50 dark:border-white/50'
                    : 'text-white hover:text-white/80 border-white/50 dark:border-white/50'
                    }`}>
                  <Phone size={20} />
                  +1-212-456-789
                </a>
              </div>
              <Separator orientation="vertical" className={`h-5 my-auto sm:block hidden ${sticky ? "bg-white/50 dark:bg-white/50" : "bg-white/50 dark:bg-white/50"}`} />
              <div>
                <Button
                  size={"lg"}
                  onClick={() => setNavbarOpen(!navbarOpen)}
                  className={`flex items-center gap-3 px-5 py-2 rounded-full font-normal hover:cursor-pointer border ${sticky
                    ? 'text-dark dark:text-white bg-background dark:hover:text-white dark:hover:bg-background hover:text-dark hover:bg-white border-dark dark:border-white'
                    : 'text-black bg-white dark:text-dark hover:bg-transparent hover:text-white border-white'
                    }`}
                  aria-label='Toggle mobile menu'>
                  <Menu size={16} />
                  Menu
                </Button>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <Sheet open={navbarOpen} onOpenChange={setNavbarOpen}>
        <SheetContent
          side="right"
          showCloseButton={false}
          className="bg-background p-6 overflow-auto no-scrollbar border-none flex flex-col gap-12"
        >
          {/* Header / Close Button */}
          <SheetHeader>
            <SheetClose className='absolute top-4 right-4 rounded-full dark:bg-white bg-black dark:text-black text-white p-2'>
              <X size={24} />
            </SheetClose>
          </SheetHeader>

          {/* Navigation */}
          <nav>
            <ul className='flex flex-col gap-4'>
              {navData?.map((item, index) => (
                <SheetClose key={index} className="w-fit">
                  <NavLink item={item} />
                </SheetClose>
              ))}
            </ul>
          </nav>

          {/* Contact & Socials */}
          <div className="flex flex-col gap-12 text-foreground">
            {/* Contact */}
            <div className="flex flex-col gap-1">
              <p className="text-lg font-normal text-muted-foreground">
                Contact
              </p>
              <a href="#" className="text-base font-medium hover:text-primary">
                hello@homely.com
              </a>
              <a href="#" className="text-base font-medium hover:text-primary">
                +1-212-456-7890
              </a>
            </div>

            {/* Socials */}
            <div className="flex flex-col gap-1">
              <p className="text-lg font-normal text-muted-foreground">
                Socials
              </p>
              <a href="#" className="text-base font-medium hover:text-primary">
                X / Twitter
              </a>
              <a href="#" className="text-base font-medium hover:text-primary">
                Facebook
              </a>
              <a href="#" className="text-base font-medium hover:text-primary">
                Instagram
              </a>
            </div>
          </div>
        </SheetContent>
      </Sheet>
    </motion.header>
  );
};

export default Navbar

"use client";

import { ComponentPropsWithoutRef } from "react";
import { cn } from "@/lib/utils";

interface MarqueeProps extends ComponentPropsWithoutRef<"div"> {
  className?: string;
  reverse?: boolean;
  pauseOnHover?: boolean;
  children: React.ReactNode;
  vertical?: boolean;
  repeat?: number;
  duration?: string;
  gap?: string;
}

export function Marquee({
  className,
  reverse = false,
  pauseOnHover = false,
  vertical = false,
  repeat = 4,
  duration = "40s",
  gap = "1rem",
  children,
  ...props
}: MarqueeProps) {
  // Repeat children inline for seamless scrolling
  const repeatedChildren = Array(repeat)
    .fill(0)
    .map((_, i) => (
      <div key={i} className="flex shrink-0" style={{ gap }}>
        {children}
      </div>
    ));

  return (
    <>
      {/* Inline CSS */}
      <style>{`
        @keyframes marquee {
          from { transform: translateX(0); }
          to { transform: translateX(calc(-100% - ${gap})); }
        }
        @keyframes marquee-vertical {
          from { transform: translateY(0); }
          to { transform: translateY(calc(-100% - ${gap})); }
        }

        .marquee {
          display: flex;
          animation: marquee ${duration} linear infinite;
          animation-direction: ${reverse ? "reverse" : "normal"};
        }

        .marquee-vertical {
          display: flex;
          flex-direction: column;
          animation: marquee-vertical ${duration} linear infinite;
          animation-direction: ${reverse ? "reverse" : "normal"};
        }

        .marquee-hover:hover,
        .marquee-vertical-hover:hover {
          animation-play-state: paused;
        }
      `}</style>

      <div
        {...props}
        className={cn(
          "overflow-hidden flex",
          vertical ? "flex-col" : "flex-row",
          className,
        )}
      >
        <div
          className={cn(
            vertical
              ? pauseOnHover
                ? "marquee-vertical-hover marquee-vertical"
                : "marquee-vertical"
              : pauseOnHover
                ? "marquee-hover marquee"
                : "marquee",
          )}
        >
          {repeatedChildren}
        </div>
      </div>
    </>
  );
}

import HeroSection from "@/components/shadcn-space/blocks/hero-02/hero"
