"use client";

import { motion } from "motion/react";
import { Icon } from "@iconify/react";
import { Button } from "@/components/ui/button";

export default function Marquee() {
  const data = [
    {
      bg: "bg-indigo-100",
      icon: "solar:dollar-minimalistic-linear",
      color: "bg-indigo-500",
      title: "Total Orders",
      price: "16,689",
      link: "#",
    },
    {
      bg: "bg-rose-100",
      icon: "ic:baseline-sync-problem",
      color: "bg-rose-400",
      title: "Cancel Orders",
      price: "64",
      link: "#",
    },
    {
      bg: "bg-green-100",
      icon: "ic:outline-forest",
      color: "bg-green-500",
      title: "Total Income ",
      price: "$36,715",
      link: "#",
    },
  ];

  return (
    <div className="overflow-hidden flex items-center justify-center max-w-5xl">
      <motion.div
        animate={{ x: ["0%", "-29%"] }}
        transition={{
          duration: 10,
          ease: "linear",
          repeat: Infinity,
        }}
        className="flex flex-nowrap gap-4"
      >
        {data.map((item, index) => (
          <div className="shrink-0" key={index}>
            <div className={`text-center px-5 py-7 rounded-lg w-64 ${item.bg}`}>
              <span
                className={`h-12 w-12 mx-auto flex items-center justify-center rounded-lg ${item.color}`}
              >
                <Icon icon={item.icon} className="text-white" height={24} />
              </span>
              <p className="text-sm font-normal mt-4 mb-2">{item.title}</p>
              <h4 className="text-base! font-medium!">{item.price}</h4>
              <Button
                size="sm"
                className="w-fit mx-auto mt-5 bg-white hover:bg-neutral-800 text-ld font-semibold hover:text-white shadow-sm py-1 px-3 dark:bg-neutral-800 dark:hover:bg-neutral-700"
              >
                <a href={item.link}>View Details</a>
              </Button>
            </div>
          </div>
        ))}
        {data.map((item, index) => (
          <div className="shrink-0 " key={index}>
            <div className={`text-center px-5 py-7 rounded-lg w-64 ${item.bg}`}>
              <span
                className={`h-12 w-12 mx-auto flex items-center justify-center rounded-lg ${item.color}`}
              >
                <Icon icon={item.icon} className="text-white" height={24} />
              </span>
              <p className="text-sm font-normal mt-4 mb-2">{item.title}</p>
              <h4 className="text-base! font-medium!">{item.price}</h4>
              <Button
                size="sm"
                className="w-fit mx-auto mt-5 bg-white hover:bg-neutral-800 text-ld font-semibold hover:text-white shadow-sm py-1 px-3 dark:bg-neutral-800 dark:hover:bg-neutral-700"
              >
                <a href={item.link}>View Details</a>
              </Button>
            </div>
          </div>
        ))}
      </motion.div>
    </div>
  );
}
