"use client"

import * as React from "react"
import { Icon } from "@iconify/react"
import {
    Sidebar,
    SidebarContent,
    SidebarGroup,
    SidebarGroupContent,
    SidebarGroupLabel,
    SidebarHeader,
    SidebarMenu,
    SidebarMenuButton,
    SidebarMenuItem,
    SidebarProvider,
    SidebarRail,
    SidebarTrigger,
} from "@/components/ui/sidebar"
import { ScrollArea } from "@/components/ui/scroll-area"


type MenuItem = {
    label: string
    icon?: string
    href?: string
    children?: MenuItem[]
}

const menuConfig: { label: string; items: MenuItem[] }[] = [
    {
        label: "Dashboards",
        items: [
            {
                label: "Dashboards",
                icon: "solar:atom-line-duotone",
                children: [
                    { label: "Dashboard 1", href: "#", },
                    { label: "Dashboard 2", href: "#" },
                    { label: "Dashboard 3", href: "#" },
                ],
            },
        ],
    },
    {
        label: "Frontend Pages",
        items: [
            {
                label: "Pages",
                icon: "solar:home-smile-line-duotone",
                children: [
                    { label: "Homepage", href: "#" },
                    { label: "About Us", href: "#" },
                    { label: "Blog", href: "#" },
                    { label: "Blog Details", href: "#" },
                ],
            },
        ],
    },
    {
        label: "Apps",
        items: [
            {
                label: "AI",
                icon: "solar:star-circle-linear",
                children: [
                    { label: "Chat", href: "#" },
                    { label: "Image", href: "#" }
                ],
            },
            {
                label: "Ecommerce",
                icon: "solar:cart-3-line-duotone",
                children: [
                    { label: "Products", href: "#" },
                    { label: "Orders", href: "#" },
                    { label: "Customers", href: "#" },
                ],
            },
            {
                label: "Chats",
                icon: "solar:chat-round-line-duotone",
                href: "#",
            },
            {
                label: "Blogs",
                icon: "solar:widget-add-line-duotone",
                children: [
                    { label: "Blog Post", href: "#" },
                    { label: "Blog Detail", href: "#" },
                    { label: "Blog Edit", href: "#" },
                    { label: "Blog Create", href: "#" },
                    { label: "Manage Blogs", href: "#" },
                ],
            },
            { icon: "solar:chat-round-line-line-duotone", label: "Chats", href: "#" },
            {
                label: "User Profile",
                icon: "solar:shield-user-outline",
                children: [
                    { label: "Profile", href: "#" },
                    { label: "Followers", href: "#" },
                    { label: "Friends", href: "#" },
                    { label: "Gallery", href: "#" }
                ],
            },
            { icon: "solar:bill-check-outline", label: "Invoice", href: "#" },
            { icon: "solar:document-text-outline", label: "Notes", href: "#" },
        ],
    },
    {
        label: "Pages",
        items: [
            { icon: "solar:settings-minimalistic-line-duotone", label: "Account Setting", href: "#" },
            { icon: "solar:question-circle-line-duotone", label: "FAQ", href: "#" },
            { icon: "solar:dollar-minimalistic-linear", label: "Pricing", href: "#" },
            { icon: "solar:accessibility-broken", label: "Roll Base Access", href: "#" },
            { icon: "solar:home-add-linear", label: "Integrations", href: "#" },
        ],
    },
]

const firstMenuItem = menuConfig[0]?.items[0]
const firstChildItem = firstMenuItem?.children?.[0]

const SidebarDropdownItem = ({
    item,
    activeParent,
    activeChild,
    setActiveParent,
    setActiveChild,
}: {
    item: MenuItem
    activeParent: string
    activeChild: string
    setActiveParent: (val: string) => void
    setActiveChild: (val: string) => void
}) => {
    const hasChildren = !!item.children?.length
    const isParentActive = item.label === activeParent

    const [open, setOpen] = React.useState(isParentActive)

    return (
        <SidebarMenuItem>
            {/* ---------------- Parent ---------------- */}
            <SidebarMenuButton
                onClick={() => {
                    if (hasChildren) {
                        setOpen(!open)
                        setActiveParent(item.label)
                    }
                }}
                className={`
                    flex items-center justify-between h-auto! px-4! py-2.5!
                    ${isParentActive ? "bg-primary! text-primary-foreground!" : ""}
                `}
            >
                <div className="flex items-center gap-3">
                    {item.icon && <Icon icon={item.icon} width={18} height={18} />}
                    <span>{item.label}</span>
                </div>

                {hasChildren && (
                    <Icon
                        icon="solar:alt-arrow-down-line-duotone"
                        className={`h-4 w-4 transition-transform ${open ? "rotate-180" : ""
                            }`}
                    />
                )}
            </SidebarMenuButton>

            {/* ---------------- Children ---------------- */}
            {hasChildren && open && (
                <SidebarMenu className="ml-0.5 mt-1 pl-3 pr-3">
                    {item.children!.map((child, index) => (
                        <SidebarMenuItem key={index}>
                            <SidebarMenuButton >
                                <a
                                    href={child.href}
                                    onClick={() => {
                                        setActiveParent(item.label)
                                        setActiveChild(child.label)
                                    }}
                                    className={`
                                        flex items-center gap-3 rounded-md px-3 py-2
                                        ${activeChild === child.label
                                            ? "bg-accent text-accent-foreground"
                                            : ""
                                        }
                                    `}
                                >
                                    <span className="h-2 w-2 rounded-full border border-muted-foreground" />
                                    <span>{child.label}</span>
                                </a>
                            </SidebarMenuButton>
                        </SidebarMenuItem>
                    ))}
                </SidebarMenu>
            )}
        </SidebarMenuItem>
    )
}

/* -------------------------------------------------------------------------- */
/*                                   Page                                     */
/* -------------------------------------------------------------------------- */

const Page = () => {
    const [activeParent, setActiveParent] = React.useState(
        firstMenuItem?.label ?? ""
    )

    const [activeChild, setActiveChild] = React.useState(
        firstChildItem?.label ?? ""
    )

    return (
        <SidebarProvider>
            <Sidebar>
                {/* ---------------- Header ---------------- */}
                <SidebarHeader>
                    <SidebarMenu>
                        <SidebarMenuItem>
                            <SidebarMenuButton size="lg" >
                                <a href="#">
                                    <img
                                        src="https://images.shadcnspace.com/assets/logo/shadcnspace.svg"
                                        className="dark:hidden w-[160px]"
                                    />
                                    <img
                                        src="https://images.shadcnspace.com/assets/logo/shadcnspace-white.svg"
                                        className="hidden dark:block w-[160px]"
                                    />
                                </a>
                            </SidebarMenuButton>
                        </SidebarMenuItem>
                    </SidebarMenu>
                </SidebarHeader>

                {/* ---------------- Content ---------------- */}
                <SidebarContent className="overflow-hidden">
                    <ScrollArea className="h-[calc(100vh-85px)]">
                        {menuConfig.map((group, index) => (
                            <SidebarGroup key={index}>
                                <SidebarGroupLabel className="text-xs font-medium uppercase">
                                    {group.label}
                                </SidebarGroupLabel>

                                <SidebarGroupContent>
                                    <SidebarMenu>
                                        {group.items.map((item, idx) => (
                                            <SidebarDropdownItem
                                                key={idx}
                                                item={item}
                                                activeParent={activeParent}
                                                activeChild={activeChild}
                                                setActiveParent={setActiveParent}
                                                setActiveChild={setActiveChild}
                                            />
                                        ))}
                                    </SidebarMenu>
                                </SidebarGroupContent>
                            </SidebarGroup>
                        ))}
                    </ScrollArea>
                </SidebarContent>

                <SidebarRail />
            </Sidebar>

            {/* ---------------- Main ---------------- */}
            <div className="flex flex-1 flex-col">
                <header className="sticky top-0 z-50 flex h-14 items-center border-b px-4">
                    <SidebarTrigger />
                </header>
                <main className="flex-1 p-4" />
            </div>
        </SidebarProvider>
    )
}

export default Page
