"use client"
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"
import {
    SidebarGroup,
    SidebarMenu,
    SidebarMenuButton,
    SidebarMenuItem,
    useSidebar,
} from "@/components/ui/sidebar"
import { Icon } from "@iconify/react"

export function NavMain({
    items,
}: {
    items: {
        title: string
        url: string
        icon?: string
        isActive?: boolean
        items?: {
            title: string
            url: string
            icon?: string
        }[]
    }[]
}) {
    const { isMobile } = useSidebar()

    return (
        <SidebarGroup>
            <SidebarMenu>
                {items.map((item) => (
                    <DropdownMenu key={item.title}>
                        <SidebarMenuItem>
                            <DropdownMenuTrigger >
                                <SidebarMenuButton className="data-[state=open]:bg-sidebar-accent data-[state=open]:text-sidebar-accent-foreground focus-visible:shadow-none! focus-visible:ring-0! text-foreground font-normal">
                                    {item.title}
                                    <Icon icon="solar:menu-dots-bold" className="ml-auto cursor-pointer" />
                                </SidebarMenuButton>
                            </DropdownMenuTrigger>
                            {item.items?.length ? (
                                <DropdownMenuContent
                                    side={isMobile ? "bottom" : "right"}
                                    align={isMobile ? "end" : "start"}
                                    className="min-w-56 rounded-lg"
                                >
                                    {item.items.map((item) => (
                                        <>
                                            <DropdownMenuItem
                                                key={item.title}
                                                className='flex gap-3 font-normal cursor-pointer'>
                                                <a href={item.url} className="flex gap-3">
                                                    <Icon icon={`${item.icon}`} height={18} className="text-foreground" />
                                                    <span>{item.title}</span>
                                                </a>
                                            </DropdownMenuItem>
                                        </>
                                    ))}
                                </DropdownMenuContent>
                            ) : null}
                        </SidebarMenuItem>
                    </DropdownMenu>
                ))}
            </SidebarMenu>
        </SidebarGroup>
    )
}

