"use client"

import { useState } from "react"
import {
    Collapsible,
    CollapsibleContent,
    CollapsibleTrigger,
} from "@/components/ui/collapsible"
import {
    SidebarGroup,
    SidebarMenu,
    SidebarMenuButton,
    SidebarMenuItem,
    SidebarMenuSub,
    SidebarMenuSubButton,
    SidebarMenuSubItem,
} from "@/components/ui/sidebar"
import { Icon } from "@iconify/react"

export function NavMain({
    items,
}: {
    items: {
        title: string
        url: string
        icon?: string
        isActive?: boolean
        items?: {
            title: string
            url: string
        }[]
    }[]
}) {
    const [activeMenu, setActiveMenu] = useState<string | null>(null)
    const [activeSubMenu, setActiveSubMenu] = useState<string | null>(null)

    return (
        <SidebarGroup>
            <SidebarMenu>
                {items.map((item) => (
                    <Collapsible
                        key={item.title}
                        open={activeMenu === item.title}
                        className="group/collapsible"
                    >
                        <SidebarMenuItem>
                            <CollapsibleTrigger >
                                <SidebarMenuButton
                                    tooltip={item.title}
                                    onClick={() => {
                                        setActiveMenu(item.title)
                                        setActiveSubMenu(null)
                                    }}
                                    data-active={activeMenu === item.title}
                                    className="cursor-pointer data-[state=open]:bg-primary! data-[state=open]:text-white! data-[active=true]:bg-primary data-[active=true]:text-primary-foreground"
                                >
                                    {item.icon && <Icon icon={item.icon} />}
                                    <span>{item.title}</span>
                                    <Icon
                                        icon="solar:alt-arrow-down-outline"
                                        className="ml-auto transition-transform duration-200 group-data-[state=open]/collapsible:rotate-180"
                                    />
                                </SidebarMenuButton>
                            </CollapsibleTrigger>

                            <CollapsibleContent>
                                <SidebarMenuSub className="mt-0.5">
                                    {item.items?.map((subItem) => (
                                        <SidebarMenuSubItem key={subItem.title}>
                                            <SidebarMenuSubButton
                                                onClick={() => {
                                                    setActiveMenu(item.title)
                                                    setActiveSubMenu(subItem.title)
                                                }}
                                                className={`cursor-pointer ${activeSubMenu === subItem.title ? "bg-accent text-accent-foreground" : ""}`}
                                            >
                                                <a href={subItem.url}>
                                                    <span>{subItem.title}</span>
                                                </a>
                                            </SidebarMenuSubButton>
                                        </SidebarMenuSubItem>
                                    ))}
                                </SidebarMenuSub>
                            </CollapsibleContent>
                        </SidebarMenuItem>
                    </Collapsible>
                ))}
            </SidebarMenu>
        </SidebarGroup>
    )
}

