import Statistics03 from "@/components/shadcn-space/blocks/statistics-03/statistics03"

export default function Page() {
    return (
        <div>
            <Statistics03 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });

const ChartData: any = {
    series: [
        {
            name: "Project",
            data: [3, 5, 5, 7, 6, 5, 3, 5, 3],
            labels: ["2012", "2013", "2014", "2015", "2016", "2017"],
        },
    ],

    chart: {
        fontFamily: "inherit",
        height: 55,
        type: "bar",
        offsetX: -3,
        toolbar: {
            show: false,
        },
        sparkline: {
            enabled: true,
        },
    },
    colors: ["#fff"],
    plotOptions: {
        bar: {
            horizontal: false,
            columnWidth: "55%",
            endingShape: "flat",
            borderRadius: 4,
        },
    },
    tooltip: {
        theme: "dark",
        followCursor: true,
    },
};

const Statistics03 = () => {
    return (
        <div className="min-h-screen mx-10 flex flex-col items-center justify-center">
            <Card className="shadow-none p-0 overflow-hidden border-0 bg-red-500/10">
                <div className="p-6">
                    <p className="text-sm font-normal text-muted-foreground mb-2">Projects</p>
                    <div className="flex gap-3 align-self mb-4">
                        <h5 className="text-foreground text-2xl font-semibold">78,298</h5>
                        <span className="text-sm font-normal pt-1 text-muted-foreground">+31.8%</span>
                    </div>
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="bar"
                        height="55px"
                        width="100%"
                    />
                </div>
            </Card>
        </div>
    );
};

export default Statistics03;
