import Statistics04 from "@/components/shadcn-space/blocks/statistics-04/statistics04"

export default function Page() {
    return (
        <div>
            <Statistics04 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    series: [
        {
            name: "April 07 ",
            data: [0, 20, 15, 19, 14, 25, 30],
        },
        {
            name: "Last Week",
            data: [0, 8, 19, 13, 26, 16, 25],
        },
    ],
    chart: {
        fontFamily: "inherit",
        height: 100,
        type: "line",
        toolbar: {
            show: false,
        },
        sparkline: {
            enabled: true,
        },
    },
    colors: ["var(--color-blue-500)", "var(--color-blue-100)"],
    grid: {
        show: false,
    },
    stroke: {
        curve: "smooth",
        colors: ["var(--color-blue-500)", "var(--color-blue-100)"],
        width: 2,
    },
    markers: {
        colors: ["var(--color-blue-500)", "var(--color-blue-100)"],
        strokeColors: "transparent",
    },
    tooltip: {
        theme: "dark",
        x: {
            show: false,
        },
        followCursor: true,
    },
};
const Statistics04 = () => {
    return (
        <div className="min-h-screen mx-10 flex flex-col items-center justify-center">
            <Card className="p-7 overflow-hidden">
                <div className="flex justify-between align-baseline">
                    <div>
                        <h5 className="text-xl font-semibold text-foreground">Customers</h5>
                        <p className="text-sm font-medium text-muted-foreground">Last 7 days</p>
                    </div>
                    <span className="text-sm font-medium text-teal-400">+26.5%</span>
                </div>
                <div className="mt-5">
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="line"
                        height="100px"
                        width="100%"
                    />
                </div>
                <div className="mt-8">
                    <div className="flex justify-between">
                        <div className="flex gap-2 text-sm items-center">
                            <span className="bg-blue-500 rounded-full h-2 w-2"></span>
                            <h6 className="text-sm font-medium text-foreground">April 07 - April 14</h6>
                        </div>
                        <h6 className="text-sm font-medium text-foreground">6,380</h6>
                    </div>
                    <div className="flex justify-between mt-2">
                        <div className="flex gap-2 text-sm  items-center">
                            <span className="bg-[#3B82F63a] rounded-full h-2 w-2"></span>
                            <h6 className="text-sm font-medium text-foreground">Last Week</h6>
                        </div>
                        <h6 className="text-sm font-medium text-foreground">4,298</h6>
                    </div>
                </div>
            </Card>
        </div>
    );
};

export default Statistics04;
