import Statistics05 from "@/components/shadcn-space/blocks/statistics-05/statistics05"

export default function Page() {
    return (
        <div>
            <Statistics05 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    series: [50, 80, 30],
    labels: ["36%", "10%", "36%"],
    chart: {
        type: "radialBar",
        height: 230,
        fontFamily: "inherit",
        foreColor: "#c6d1e9",
    },
    plotOptions: {
        radialBar: {
            inverseOrder: false,
            startAngle: 0,
            endAngle: 270,
            hollow: {
                margin: 1,
                size: "40%",
            },
            dataLabels: {
                show: false,
            },
        },
    },
    legend: {
        show: false,
    },
    stroke: { width: 1, lineCap: "round" },
    tooltip: {
        enabled: false,
        fillSeriesColor: false,
    },
    colors: [
        "var(--color-blue-500)", "var(--color-teal-400)", "var(--color-red-500)"
    ],
};

const Statistics05 = () => {
    return (
        <div className="min-h-screen mx-10 flex flex-col items-center justify-center">
            <Card className="p-7 overflow-hidden">
                <div>
                    <h5 className="text-xl font-semibold text-foreground">Sales Overview</h5>
                    <p className="text-sm font-medium text-muted-foreground">Last 7 days</p>
                </div>
                <div className=" relative">
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="radialBar"
                        height="230px"
                        width="100%"
                    />
                    <span className="absolute top-0 mx-auto left-0 right-0 w-5 text-sm opacity-90">0%</span>
                    <span className="absolute top-1/2 right-0 me-2 w-5 text-sm opacity-90">25%</span>
                    <span className="absolute bottom-0 mx-auto left-0 right-0 w-5 text-sm opacity-90">50%</span>
                    <span className="absolute top-1/2 left-0 ms-2 w-5 text-sm opacity-90">75%</span>
                </div>
            </Card>
        </div>
    );
};

export default Statistics05;
