import Statistics06 from "@/components/shadcn-space/blocks/statistics-06/statistics06"

export default function Page() {
    return (
        <div>
            <Statistics06 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import { Icon } from "@iconify/react";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    series: [
        {
            name: "settlements",
            data: [
                40, 40, 80, 80, 30, 30, 10, 10, 30, 30, 100, 100, 20, 20, 140, 140,
            ],
        },
    ],
    chart: {
        fontFamily: "inherit",
        type: "line",
        height: 315,
        toolbar: { show: !1 },
    },
    legend: { show: !1 },
    dataLabels: { enabled: !1 },
    stroke: {
        curve: "smooth",
        show: !0,
        width: 2,
        colors: ["var(--color-blue-500)"],
    },
    xaxis: {
        categories: [
            "1W",
            "",
            "3W",
            "",
            "5W",
            "6W",
            "7W",
            "8W",
            "9W",
            "",
            "11W",
            "",
            "13W",
            "",
            "15W",
        ],
        axisBorder: { show: !1 },
        axisTicks: { show: !1 },
        tickAmount: 6,
        labels: {
            rotate: 0,
            rotateAlways: !0,
            style: { fontSize: "10px", colors: "#98A4AE", fontWeight: "600" },
        },
    },
    yaxis: {
        show: false,
        tickAmount: 3,
    },
    tooltip: {
        theme: "dark",
    },
    colors: ["var(--color-blue-500)"],
    grid: {
        borderColor: "var(--color-muted)",
        strokeDashArray: 4,

        xaxis: {
            lines: {
                show: false,
            },
        },
        yaxis: {
            lines: {
                show: false,
            },
        },
    },
    markers: {
        strokeColor: ["var(--color-blue-500)"],
        strokeWidth: 3,
    },
};
const Statistics06 = () => {
    return (
        <div className="min-h-screen mx-10 flex flex-col items-center justify-center">
            <Card className="p-7 border-0 overflow-hidden bg-blue-500/10  h-full">
                <div className="flex gap-4 items-center">
                    <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-white rounded-lg">
                        <Icon
                            icon="solar:box-linear"
                            className="text-blue-500"
                            height={24}
                        />
                    </span>
                    <div>
                        <p className="text-sm font-normal mb-1 text-muted-foreground">Total settlements</p>
                        <h5 className="text-lg text-foreground font-semibold">$122,580</h5>
                    </div>
                </div>
                <div>
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="line"
                        height="305px"
                        width="100%"
                    />
                </div>
                <div className="grid grid-cols-12">
                    <div className="col-span-6 text-center">
                        <p className="text-sm font-normal mb-1 text-muted-foreground">Total balance</p>
                        <h5 className="text-lg text-foreground font-semibold">$122,580</h5>
                    </div>
                    <div className="col-span-6 text-center">
                        <p className="text-sm font-normal mb-1 text-muted-foreground">Withdrawals</p>
                        <h5 className="text-lg text-foreground font-semibold">$31,640</h5>
                    </div>
                </div>
            </Card>
        </div>
    );
};

export default Statistics06;
