import Statistics07 from "@/components/shadcn-space/blocks/statistics-07/statistics07"

export default function Page() {
    return (
        <div>
            <Statistics07 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import { Icon } from "@iconify/react";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    series: [
        {
            name: "Site A",
            data: [29, 52, 38, 47, 56, 41, 46],
        },
        {
            name: "Site B",
            data: [71, 71, 71, 71, 71, 71, 71],
        },
    ],
    chart: {
        fontFamily: "inherit",
        foreColor: "#adb0bb",
        type: "bar",
        height: 98,
        stacked: true,
        offsetX: 0,
        toolbar: {
            show: false,
        },
        sparkline: {
            enabled: true,
        },
    },
    colors: ["var(--color-white)", "rgba(255,255,255,0.5)"],
    plotOptions: {
        bar: {
            horizontal: false,
            columnWidth: "26%",
            borderRadius: [3],
            borderRadiusApplication: "end",
            borderRadiusWhenStacked: "all",
        },
    },
    dataLabels: {
        enabled: false,
    },
    tooltip: {
        theme: "dark",
    },
    legend: {
        show: false,
    },
};
const Statistics07 = () => {
    return (
        <div className="min-h-screen mx-10 flex flex-col items-center justify-center">
            <Card className="p-7 border-0 bg-red-500/10">
                <div className="flex justify-between">
                    <div>
                        <p className="text-base font-normal text-muted-foreground mb-2">Subscriptions</p>
                        <div className="flex gap-3 align-self mb-4">
                            <h5 className="text-2xl font-semibold text-foreground">78,298</h5>
                            <span className="text-sm font-normal pt-1 text-muted-foreground">-12%</span>
                        </div>
                    </div>
                    <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-white dark:bg-dark rounded-lg">
                        <Icon
                            icon="solar:layers-linear"
                            className="text-red-500"
                            height={20}
                        />
                    </span>
                </div>
                <div className="rounded-bars">
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="bar"
                        height="100px"
                        width="100%"
                    />
                </div>
            </Card>
        </div>
    );
};

export default Statistics07;
