import Statistics08 from "@/components/shadcn-space/blocks/statistics-08/statistics08"

export default function Page() {
    return (
        <div>
            <Statistics08 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import { Icon } from "@iconify/react";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    chart: {
        id: "customers",
        type: "area",
        height: 95,
        sparkline: {
            enabled: true,
        },
        group: "sparklines",
        fontFamily: "inherit",
        foreColor: "#14b8a6",
    },
    series: [
        {
            name: "Users",
            color: "#2DD4BF",
            data: [36, 45, 31, 47, 38, 43],
        },
    ],
    stroke: {
        curve: "smooth",
        width: 2,
    },
    fill: {
        type: "gradient",
        color: "#2DD4BF",

        gradient: {
            shadeIntensity: 0,
            inverseColors: false,
            opacityFrom: 0.2,
            opacityTo: 0.8,
            stops: [0, 100],
        },
    },

    markers: {
        size: 0,
    },
    tooltip: {
        theme: "dark",
        fixed: {
            enabled: true,
            position: "right",
        },
        x: {
            show: false,
        },
    },
};
const Statistics08 = () => {
    return (
        <div className="min-h-screen mx-10 flex flex-col items-center justify-center">
            <Card className="p-0 border-0 bg-teal-400/10 overflow-hidden">
                <div className="flex justify-between py-7 px-6">
                    <div>
                        <p className="text-base font-normal text-muted-foreground mb-2">Users</p>
                        <div className="flex gap-3 align-self mb-4">
                            <h5 className="text-2xl font-semibold text-foreground">14,872</h5>
                            <span className="text-sm font-normal pt-1 text-muted-foreground">+6.4%</span>
                        </div>
                    </div>
                    <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-white dark:bg-dark rounded-lg">
                        <Icon
                            icon="solar:pie-chart-3-line-duotone"
                            className="text-teal-400"
                            height={20}
                        />
                    </span>
                </div>
                <div>
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="area"
                        height="98px"
                        width="100%"
                    />
                </div>
            </Card>
        </div>
    );
};

export default Statistics08;
