import Statistics09 from "@/components/shadcn-space/blocks/statistics-09/statistics09"

export default function Page() {
    return (
        <div>
            <Statistics09 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import { Icon } from "@iconify/react";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    series: [
        {
            name: "2024",
            data: [1.2, 2.7, 1, 3.6, 2.1, 2.7, 2.2, 1.3, 2.5],
        },
        {
            name: "2023",
            data: [-2.8, -1.1, -2.5, -1.5, -2.3, -1.9, -1, -2.1, -1.3],
        },
    ],
    chart: {
        toolbar: {
            show: false,
        },
        type: "bar",
        fontFamily: "inherit",
        foreColor: "#adb0bb",
        height: 295,
        stacked: true,
        offsetX: -15,
    },
    colors: ["var(--color-blue-500)", "var(--color-red-500)"],
    plotOptions: {
        bar: {
            horizontal: false,
            barHeight: "60%",
            columnWidth: "15%",
            borderRadius: [6],
            borderRadiusApplication: "end",
            borderRadiusWhenStacked: "all",
        },
    },
    dataLabels: {
        enabled: false,
    },
    legend: {
        show: false,
    },
    grid: {
        show: true,
        padding: {
            top: 0,
            bottom: 0,
            right: 0,
        },
        borderColor: "var(--color-muted)",
        xaxis: {
            lines: {
                show: true,
            },
        },
        yaxis: {
            lines: {
                show: true,
            },
        },
    },
    yaxis: {
        min: -5,
        max: 5,
        tickAmount: 4,
    },
    xaxis: {
        axisBorder: {
            show: false,
        },
        axisTicks: {
            show: false,
        },
        categories: [
            "Jan",
            "Feb",
            "Mar",
            "Apr",
            "May",
            "Jun",
            "July",
            "Aug",
            "Sep",
        ],
        labels: {
            style: { fontSize: "13px", colors: "#adb0bb", fontWeight: "400" },
        },
    },

    tooltip: {
        theme: "dark",
    },
};
const Statistics09 = () => {
    return (
        <div className="min-h-screen  mx-7 flex flex-col items-center justify-center">
            <Card className="p-7 max-w-3xl w-full overflow-hidden text-card-foreground">
                <div className="md:flex justify-between items-center">
                    <div>
                        <h5 className="text-lg font-semibold text-foreground">Revenue Forecast</h5>
                        <p className="text-sm font-normal text-muted-foreground">Overview of Profit</p>
                    </div>
                    <div className="flex gap-5 items-center md:mt-0 mt-4">
                        <div className="flex gap-2 text-sm   items-center">
                            <span className="bg-blue-500 rounded-full h-2 w-2"></span>
                            <span className="text-muted-foreground">2024</span>
                        </div>
                        <div className="flex gap-2 text-sm text-ld items-center">
                            <span className="bg-red-500 rounded-full h-2 w-2"></span>
                            <span className="text-muted-foreground">2023</span>
                        </div>
                    </div>
                </div>
                <div className="rounded-bars">
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="bar"
                        height="298px"
                        width="100%"
                    />
                </div>
                <div className="flex md:flex-row flex-col gap-3">
                    <div className="md:basis-1/3 basis-full">
                        <div className="flex gap-3 items-center">
                            <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-muted dark:bg-dark rounded-lg">
                                <Icon
                                    icon="solar:pie-chart-2-linear"
                                    className="text-ld"
                                    height={24}
                                />
                            </span>
                            <div>
                                <p className="text-sm text-muted-foreground">Total</p>
                                <h5 className="font-medium text-lg text-foreground">$96,640</h5>
                            </div>
                        </div>
                    </div>
                    <div className="md:basis-1/3 basis-full">
                        <div className="flex gap-3 items-center">
                            <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-blue-500/10 rounded-lg">
                                <Icon
                                    icon="solar:dollar-minimalistic-linear"
                                    className="text-blue-500"
                                    height={24}
                                />
                            </span>
                            <div>
                                <p className="text-sm text-muted-foreground">Profit</p>
                                <h5 className="font-medium text-lg text-foreground">$48,820</h5>
                            </div>
                        </div>
                    </div>
                    <div className="md:basis-1/3 basis-full">
                        <div className="flex gap-3 items-center">
                            <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-red-500/10 rounded-lg">
                                <Icon
                                    icon="solar:database-linear"
                                    className="text-red-500"
                                    height={24}
                                />
                            </span>
                            <div>
                                <p className="text-sm text-muted-foreground">Earnings</p>
                                <h5 className="font-medium text-lg text-foreground">$48,820</h5>
                            </div>
                        </div>
                    </div>
                </div>
            </Card>
        </div >
    );
};

export default Statistics09;
