import Statistics10 from "@/components/shadcn-space/blocks/statistics-10/statistics10"

export default function Page() {
    return (
        <div>
            <Statistics10 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    chart: {
        id: "annual-profit",
        type: "area",
        height: 80,
        sparkline: {
            enabled: true,
        },
        group: "sparklines",
        fontFamily: "inherit",
        foreColor: "#adb0bb",
    },
    series: [
        {
            name: "Users",
            color: "#3B82F6",
            data: [25, 66, 20, 40, 12, 58, 20],
        },
    ],
    stroke: {
        curve: "smooth",
        width: 2,
    },
    fill: {
        type: "gradient",
        color: "#3B82F6",

        gradient: {
            shadeIntensity: 0,
            inverseColors: false,
            opacityFrom: 0.1,
            opacityTo: 0.8,
            stops: [0, 100],
        },
    },

    markers: {
        size: 0,
    },
    tooltip: {
        theme: "dark",
        fixed: {
            enabled: true,
            position: "right",
        },
        x: {
            show: false,
        },
    },
};

const Statistics10 = () => {
    return (
        <div className="min-h-screen  mx-7 flex flex-col items-center justify-center">
            <Card className="h-full p-7">
                <div>
                    <h5 className="text-lg font-semibold text-foreground">Annual Profit</h5>
                    <div className="bg-blue-500/10 mt-4 overflow-hidden rounded-md mb-1">
                        <div className="py-7 px-6 flex justify-between items-center ">
                            <p className="text-sm font-normal text-muted-foreground">Conversion Rate</p>
                            <h4 className="text-3xl font-normal text-foreground">18.4%</h4>
                        </div>
                        <Chart
                            options={ChartData}
                            series={ChartData.series}
                            type="area"
                            height="60px"
                            width="100%"
                            className="mt-4"
                        />
                    </div>
                    <div className="flex items-center justify-between py-4 text-sm border-b border-ld">
                        <div>
                            <span className="font-medium text-sm text-muted-foreground">
                                Added to Cart
                            </span>
                            <p className="text-sm text-muted-foreground">5 clicks</p>
                        </div>
                        <div className="text-end">
                            <h6 className="text-base font-normal text-foreground">$21,120.70</h6>
                            <span className="text-sm text-teal-400 font-medium">+13.2%</span>
                        </div>
                    </div>
                    <div className="flex items-center justify-between py-4  border-b  border-ld">
                        <div>
                            <span className="font-medium text-sm text-muted-foreground">
                                Reached to Checkout
                            </span>
                            <p className="text-sm text-muted-foreground">12 clicks</p>
                        </div>
                        <div className="text-end">
                            <h6 className="text-base font-normal text-foreground">$16,100.00</h6>
                            <span className="text-sm text-red-500 font-medium">-7.4%</span>
                        </div>
                    </div>
                    <div className="flex items-center justify-between pt-4">
                        <div>
                            <span className="font-medium text-sm text-muted-foreground">
                                Added to Cart
                            </span>
                            <p className="text-sm text-muted-foreground">24 views</p>
                        </div>
                        <div className="text-end">
                            <h6 className="text-base font-normal text-foreground">$6,400.50</h6>
                            <span className="text-sm text-teal-400 font-medium">+9.3%</span>
                        </div>
                    </div>
                </div>
            </Card>
        </div>
    );
};

export default Statistics10;
