import Statistics11 from "@/components/shadcn-space/blocks/statistics-11/statistics11"

export default function Page() {
    return (
        <div>
            <Statistics11 />
        </div>
    )
}

"use client";
import { Icon } from "@iconify/react";
import { Progress } from "@/components/ui/progress";
import { Card } from "@/components/ui/card";
import { cn } from "@/lib/utils";

const Statistics11 = () => {
    return (
        <div className="min-h-screen mx-7 flex flex-col items-center justify-center">
            <Card className="h-full p-7 max-w-sm w-full">
                <div className="flex items-center gap-3">
                    <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-teal-400/10 rounded-lg">
                        <Icon
                            icon="solar:football-outline"
                            className="text-teal-400"
                            height={24}
                        />
                    </span>
                    <span className="font-normal text-base text-foreground">New Customers</span>
                </div>
                <div>
                    <div className="flex justify-between items-center mt-8">
                        <span className="text-sm font-normal text-muted-foreground">New goals</span>
                        <span className="text-sm font-normal text-muted-foreground">83%</span>
                    </div>
                    <Progress
                        value={45}
                        className={cn("mt-2 h-1 [&>div]:transition-all [&>div]:duration-2000", "**:data-[slot=progress-indicator]:bg-teal-500")}
                    />
                </div>
            </Card>
        </div>
    );
};

export default Statistics11;
