import Statistics12 from "@/components/shadcn-space/blocks/statistics-12/statistics12"

export default function Page() {
    return (
        <div>
            <Statistics12 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import { Icon } from "@iconify/react";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    chart: {
        id: "sparkline3",
        type: "line",
        fontFamily: "inherit",
        foreColor: "#adb0bb",
        height: 60,
        sparkline: {
            enabled: true,
        },
        group: "sparklines",
    },
    series: [
        {
            name: "Income",
            color: "var(--color-red-500)",
            data: [30, 25, 35, 20, 30, 40],
        },
    ],
    stroke: {
        curve: "smooth",
        width: 2,
    },
    markers: {
        size: 0,
    },
    tooltip: {
        theme: "dark",
        fixed: {
            enabled: true,
            position: "right",
        },
        x: {
            show: false,
        },
    },
};
const Statistics12 = () => {
    return (
        <div className="min-h-screen mx-7 flex flex-col items-center justify-center">
            <Card className="h-full p-7 max-w-sm w-full border-0">
                <div className="flex items-center gap-3">
                    <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-red-500/10 rounded-lg">
                        <Icon icon="solar:box-linear" className="text-red-500" height={24} />
                    </span>
                    <span className="font-medium text-base text-ld">Total Income</span>
                </div>
                <div className="flex gap-6 mt-4">
                    <div className="basis-3/6">
                        <h4 className="text-2xl pb-1 text-foreground">$680</h4>
                        <span className="font-medium text-sm text-teal-400">+18%</span>
                    </div>
                    <div className="basis-3/6 ms-auto">
                        <Chart
                            options={ChartData}
                            series={ChartData.series}
                            type="line"
                            height="60px"
                            width="100%"
                        />
                    </div>
                </div>
            </Card>
        </div>
    );
};

export default Statistics12;
