import Statistics13 from "@/components/shadcn-space/blocks/statistics-13/statistics13"

export default function Page() {
    return (
        <div>
            <Statistics13 />
        </div>
    )
}

"use client";
import dynamic from "next/dynamic";
import moment from "moment";
import { Card } from "@/components/ui/card";
import "./styles/style.css";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    series: [
        {
            data: [
                {
                    x: "Sun",
                    y: [
                        new Date("2024-02-27").getTime(),
                        new Date("2024-03-04").getTime(),
                    ],
                    fillColor: "var(--color-blue-500)",
                },
                {
                    x: "Mon",
                    y: [
                        new Date("2024-03-04").getTime(),
                        new Date("2024-03-10").getTime(),
                    ],
                    fillColor: "var(--color-neutral-800)",
                },
                {
                    x: "Tue",
                    y: [
                        new Date("2024-03-01").getTime(),
                        new Date("2024-03-06").getTime(),
                    ],
                    fillColor: "var(--color-red-500)",
                },
            ],
        },
    ],

    chart: {
        id: "sparkline3",
        type: "rangeBar",
        fontFamily: "inherit",
        foreColor: "#adb0bb",
        height: 300,
        toolbar: {
            show: false,
        },
    },
    plotOptions: {
        bar: {
            horizontal: true,
            distributed: true,
            dataLabels: {
                hideOverflowingLabels: false,
            },
        },
    },
    dataLabels: {
        enabled: true,
        background: {
            borderRadius: 20,
        },
        formatter: function (
            val: moment.MomentInput[],
            opts: {
                w: { globals: { labels: { [x: string]: any } } };
                dataPointIndex: string | number;
            }
        ) {
            var label = opts.w.globals.labels[opts.dataPointIndex];
            var a = moment(val[0]);
            var b = moment(val[1]);
            return label + ": " + "Meeting with Sunil";
        },
    },
    xaxis: {
        type: "datetime",
        axisBorder: {
            show: false,
        },
        axisTicks: {
            show: false,
        },
        labels: {
            style: { fontSize: "13px", colors: "#adb0bb", fontWeight: "400" },
        },
    },
    yaxis: {
        axisBorder: {
            show: false,
        },
        axisTicks: {
            show: false,
        },
        labels: {
            style: { fontSize: "13px", colors: "#adb0bb", fontWeight: "400" },
        },
    },
    grid: {
        borderColor: "var(--color-muted)",
    },
    tooltip: {
        theme: "dark",
    },
};
const Statistics13 = () => {
    return (
        <div className="min-h-screen mx-7 flex flex-col items-center justify-center">
            <Card className="h-full p-7 max-w-3xl w-full border-0">
                <div>
                    <h5 className="text-lg font-semibold">Weekly Scheduels</h5>
                </div>
                <div className="rounded-pill-bars">
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="rangeBar"
                        height="303px"
                        width="100%"
                    />
                </div>
            </Card>
        </div>
    );
};

export default Statistics13;
