import Statistics14 from "@/components/shadcn-space/blocks/statistics-14/statistics14"

export default function Page() {
    return (
        <div>
            <Statistics14 />
        </div>
    )
}

"use client";
import { Icon } from "@iconify/react";
import dynamic from "next/dynamic";
import { Separator } from "@/components/ui/separator";
import { Card } from "@/components/ui/card";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    series: [
        {
            name: "Weekly Stats",
            data: [20, 15, 18, 25, 10, 15, 20],
        },
    ],

    chart: {
        toolbar: {
            show: false,
        },

        height: 220,
        type: "bar",
        offsetX: -30,
        fontFamily: "inherit",
        foreColor: "#adb0bb",
    },
    colors: [
        "rgba(173,176,187,.15)",
        "rgba(173,176,187,.15)",
        "rgba(173,176,187,.15)",
        "#3B82F6",
        "rgba(173,176,187,.15)",
        "rgba(173,176,187,.15)",
        "rgba(173,176,187,.15)",
    ],
    plotOptions: {
        bar: {
            borderRadius: 5,
            columnWidth: "55%",
            distributed: true,
            endingShape: "rounded",
        },
    },

    dataLabels: {
        enabled: false,
    },
    legend: {
        show: false,
    },
    grid: {
        yaxis: {
            lines: {
                show: false,
            },
        },
        xaxis: {
            lines: {
                show: false,
            },
        },
    },
    xaxis: {
        categories: [
            ["Apr"],
            ["May"],
            ["June"],
            ["July"],
            ["Aug"],
            ["Sept"],
            ["Oct"],
        ],
        axisBorder: {
            show: false,
        },
        axisTicks: {
            show: false,
        },
    },
    yaxis: {
        labels: {
            show: false,
        },
    },
    tooltip: {
        theme: "dark",
    },
};

const Statistics14 = () => {
    return (
        <div className="min-h-screen mx-7 flex flex-col items-center justify-center">
            <Card className="h-full max-w-sm w-full p-7 flex flex-col justify-between gap-0 border-0">
                <div>
                    <h5 className="text-lg font-semibold text-foreground">Weekly Stats</h5>
                    <p className="text-sm font-normal text-muted-foreground">Overview of Profit</p>
                </div>
                <div className="-me-12 rtl:-me-0 rtl:-ms-12">
                    <Chart
                        options={ChartData}
                        series={ChartData.series}
                        type="bar"
                        height="220px"
                        width="100%"
                    />
                </div>
                <Separator className="my-4" />
                <div className="flex justify-between">
                    <div className="basis-3/6">
                        <div className="flex gap-3 items-center">
                            <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-red-500/10 rounded-lg">
                                <Icon
                                    icon="solar:course-down-linear"
                                    className="text-red-500"
                                    height={24}
                                />
                            </span>
                            <div>
                                <p className="text-sm text-muted-foreground">Sales</p>
                                <h5 className="font-bold text-sm text-foreground">$36,850</h5>
                            </div>
                        </div>
                    </div>
                    <div className="basis-3/6 ps-3">
                        <div className="flex gap-3 items-center">
                            <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-blue-500/10 rounded-lg">
                                <Icon
                                    icon="solar:chart-linear"
                                    className="text-blue-500"
                                    height={24}
                                />
                            </span>
                            <div>
                                <p className="text-sm text-muted-foreground">Expenses</p>
                                <h5 className="font-bold text-sm text-foreground">$4,720</h5>
                            </div>
                        </div>
                    </div>
                </div>
            </Card>
        </div>
    );
};

export default Statistics14;
