import Statistics15 from "@/components/shadcn-space/blocks/statistics-15/statistics15"

export default function Page() {
    return (
        <div>
            <Statistics15 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import { Icon } from "@iconify/react";
import dynamic from "next/dynamic";
const Chart = dynamic(() => import("react-apexcharts"), { ssr: false });
const ChartData: any = {
    series: [20, 20, 20, 20, 20],
    labels: ["245", "45", "14", "78", "95"],
    chart: {
        height: 230,
        fontFamily: "inherit",
        type: "donut",
    },
    plotOptions: {
        pie: {
            startAngle: -90,
            endAngle: 90,
            offsetY: 10,
            donut: {
                size: "90%",
            },
        },
    },
    grid: {
        padding: {
            bottom: -80,
        },
    },
    legend: {
        show: false,
    },
    dataLabels: {
        enabled: false,
        name: {
            show: false,
        },
    },
    stroke: {
        width: 2,
        colors: "var(--color-surface-ld)",
    },
    tooltip: {
        fillSeriesColor: false,
    },
    colors: [
        "var(--color-red-500)",
        "var(--color-amber-300)",
        "var(--color-amber-100)",
        "var(--color-teal-100)",
        "var(--color-teal-400)",
    ],
};

const Statistics15 = () => {
    return (
        <div className="min-h-screen mx-7 flex flex-col items-center justify-center">
            <Card className="h-full max-w-xl w-full p-7 flex flex-col justify-between gap-0 border-0">
                <div>
                    <h5 className="text-lg text-foreground font-semibold">Your Performance</h5>
                    <p className="text-sm font-normal text-muted-foreground">Last check on 25 february</p>
                </div>
                <div className="grid grid-cols-12 mt-6">
                    <div className="md:col-span-6 col-span-12">
                        <div className="flex flex-col gap-5">
                            <div className="flex gap-4 items-center">
                                <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-blue-500/10 rounded-lg">
                                    <Icon
                                        icon="solar:shop-2-linear"
                                        className="text-blue-500"
                                        height={24}
                                    />
                                </span>
                                <div>
                                    <h5 className="text-sm text-foreground font-semibold">64 new orders</h5>
                                    <p className="text-sm font-normal text-muted-foreground">Processing</p>
                                </div>
                            </div>
                            <div className="flex flex-col gap-4">
                                <div className="flex gap-4 items-center">
                                    <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-red-500/10 rounded-lg">
                                        <Icon
                                            icon="solar:filters-outline"
                                            className="text-red-500"
                                            height={24}
                                        />
                                    </span>
                                    <div>
                                        <h5 className="text-sm text-foreground font-semibold">4 orders</h5>
                                        <p className="text-sm font-normal text-muted-foreground">On hold</p>
                                    </div>
                                </div>
                            </div>
                            <div className="flex flex-col gap-4">
                                <div className="flex gap-4 items-center">
                                    <span className="h-12 w-12 shrink-0 flex items-center justify-center bg-teal-400/10 rounded-lg">
                                        <Icon
                                            icon="solar:pills-3-linear"
                                            className="text-teal-400"
                                            height={24}
                                        />
                                    </span>
                                    <div>
                                        <h5 className="text-sm text-foreground font-semibold">12 orders</h5>
                                        <p className="text-sm font-normal text-muted-foreground">Delivered</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="md:col-span-6 col-span-12 md:-mt-8 mt-4">
                        <Chart
                            options={ChartData}
                            series={ChartData.series}
                            type="donut"
                            height="230px"
                            width="100%"
                        />
                        <h4 className="text-center text-3xl font-medium md:mt-3">275</h4>
                        <p className="text-sm font-normal text-muted-foreground text-center mt-3">
                            Learn insigs how to manage all aspects of your startup.
                        </p>
                    </div>
                </div>
            </Card>
        </div>
    );
};

export default Statistics15;
