import Statistics16 from "@/components/shadcn-space/blocks/statistics-16/statistics16"

export default function Page() {
    return (
        <div>
            <Statistics16 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { Button } from '@/components/ui/button'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Tabs, TabsList, TabsTrigger, TabsContent } from '@/components/ui/tabs'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics16 = () => {
  // chart
  const optionsareachart: any = {
    chart: {
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
      fontSize: '12px',
      offsetX: 0,
      offsetY: 10,
      animations: {
        speed: 500,
      },
      toolbar: {
        show: false,
      },
    },
    colors: ['#38BDF8', '#adb0bb35'],
    dataLabels: {
      enabled: false,
    },
    fill: {
      type: 'gradient',
      gradient: {
        shadeIntensity: 0,
        inverseColors: false,
        opacityFrom: 0.3,
        opacityTo: 0.1,
        stops: [100],
      },
    },
    grid: {
      show: true,
      strokeDashArray: 3,
      borderColor: '#90A4AE50',
    },
    stroke: {
      curve: 'smooth',
      width: 2,
    },
    xaxis: {
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      min: 0,
      max: 90,
      tickAmount: 3,
    },
    legend: {
      show: false,
    },
    tooltip: {
      theme: 'dark',
    },
  }

  const areaChart = {
    series: [
      {
        type: 'area',
        name: 'This Year',
        chart: {
          foreColor: '#111c2d99',
          fontSize: 12,
          fontWeight: 500,
          dropShadow: {
            enabled: true,
            enabledOnSeries: undefined,
            top: 5,
            left: 0,
            blur: 3,
            color: '#000',
            opacity: 0.1,
          },
        },
        data: [
          { x: 'Aug', y: 25 },
          { x: 'Sep', y: 25 },
          { x: 'Oct', y: 10 },
          { x: 'Nov', y: 10 },
          { x: 'Dec', y: 45 },
          { x: 'Jan', y: 45 },
          { x: 'Feb', y: 75 },
          { x: 'Mar', y: 70 },
          { x: 'Apr', y: 35 },
        ],
      },
      {
        type: 'area',
        name: 'Last Year',
        chart: {
          foreColor: '#111c2d99',
        },

        data: [
          { x: 'Aug', y: 50 },
          { x: 'Sep', y: 50 },
          { x: 'Oct', y: 25 },
          { x: 'Nov', y: 20 },
          { x: 'Dec', y: 20 },
          { x: 'Jan', y: 20 },
          { x: 'Feb', y: 35 },
          { x: 'Mar', y: 35 },
          { x: 'Apr', y: 60 },
        ],
      },
    ],
  }

  const optionexpenses: any = {
    series: [
      {
        name: 'This year',
        data: [9, 5, 3, 7, 5, 10, 3],
      },
      {
        name: 'Last year ',
        data: [6, 3, 9, 5, 4, 6, 4],
      },
    ],
    chart: {
      fontFamily: 'inherit',
      type: 'bar',
      height: 350,
      offsetY: 10,
      offsetX: -18,
      toolbar: {
        show: false,
      },
    },
    grid: {
      show: true,
      strokeDashArray: 3,
      borderColor: 'rgba(0,0,0,.1)',
    },
    colors: ['#0ea5e9', '#8b5cf6'],
    plotOptions: {
      bar: {
        borderRadius: 5,
        horizontal: false,
        columnWidth: '30%',
        endingShape: 'rounded',
      },
    },
    dataLabels: {
      enabled: false,
    },
    stroke: {
      show: true,
      width: 5,
      colors: ['transparent'],
    },
    xaxis: {
      type: 'category',
      categories: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],

      axisTicks: {
        show: false,
      },
      axisBorder: {
        show: false,
      },
      labels: {
        style: {
          colors: '#a1aab2',
        },
      },
    },
    yaxis: {
      labels: {
        style: {
          colors: '#a1aab2',
        },
      },
      min: 0,
      max: 12,
      tickAmount: 4,
    },

    fill: {
      opacity: 1,
      colors: ['#38BDF8', '#3B82F6'],
    },
    tooltip: {
      theme: 'dark',
    },
    legend: {
      show: false,
    },

    responsive: [
      {
        breakpoint: 767,
        options: {
          stroke: {
            show: false,
            width: 5,
            colors: ['transparent'],
          },
        },
      },
    ],
  }

  return (
    <div className='min-h-screen flex flex-col items-center justify-center'>
      <Card className='w-full max-w-3xl p-6'>
        <Tabs defaultValue='Profit' className='w-full'>
          <div className='flex justify-between items-center'>
            <h5 className='text-lg font-semibold'>Sales Profit</h5>
            <TabsList className='flex flex-wrap bg-muted rounded-full w-fit'>
              <TabsTrigger
                value='Profit'
                className='py-2 px-4 rounded-full min-w-[100px] text-xs font-semibold data-[state=active]:bg-background dark:data-[state=active]:bg-dark data-[state=active]:text-foreground
             data-[state=inactive]:text-muted-foreground cursor-pointer'>
                Profit
              </TabsTrigger>

              <TabsTrigger
                value='Expenses'
                className='py-2 px-4 rounded-full min-w-[100px] text-xs font-semibold data-[state=active]:bg-background dark:data-[state=active]:bg-dark data-[state=active]:text-foreground
             data-[state=inactive]:text-muted-foreground cursor-pointer'>
                Expenses
              </TabsTrigger>
            </TabsList>
          </div>

          {/* ===== PROFIT TAB ===== */}
          <TabsContent value='Profit'>
            <div className='-ms-4 -me-3 h-[300px]'>
              <Chart
                options={optionsareachart}
                series={areaChart.series}
                type='area'
                height='300px'
                width='100%'
              />
            </div>

            <div className='sm:flex justify-between items-center mt-6'>
              <div className='flex gap-7 items-center'>
                <div className='flex gap-2 items-center'>
                  <span className='w-10 h-10 rounded-full flex items-center justify-center text-sky-400 bg-sky-400/10'>
                    <Icon
                      icon='solar:checklist-minimalistic-line-duotone'
                      height={22}
                    />
                  </span>
                  <div>
                    <h6 className='text-base font-semibold text-foreground'>
                      $63,489.50{' '}
                      <Badge className='bg-teal-400/10 rounded-full text-teal-400 text-xs'>
                        +8%
                      </Badge>
                    </h6>
                    <p className='text-sm text-muted-foreground'>
                      Profit this year
                    </p>
                  </div>
                </div>

                <div className='flex gap-2 items-center sm:my-0 my-4'>
                  <span className='w-10 h-10 rounded-full flex items-center justify-center bg-muted text-muted-foreground'>
                    <Icon
                      icon='solar:dollar-minimalistic-line-duotone'
                      height={22}
                    />
                  </span>
                  <div>
                    <h6 className='text-base font-semibold text-foreground'>
                      $38,496.00
                    </h6>
                    <p className='text-sm text-muted-foreground'>
                      Profit this year
                    </p>
                  </div>
                </div>
              </div>

              <div>
                <Button className='rounded-full px-8 bg-sky-400 text-white hover:bg-sky-400/80 cursor-pointer'>
                  View Details
                </Button>
              </div>
            </div>
          </TabsContent>

          {/* ===== EXPENSES TAB ===== */}
          <TabsContent value='Expenses'>
            <div className='h-[350px]'>
              <Chart
                options={optionexpenses}
                series={optionexpenses.series}
                type='bar'
                height='350px'
                width='100%'
              />
            </div>
          </TabsContent>
        </Tabs>
      </Card>
    </div>
  )
}

export default Statistics16
