import Statistics17 from "@/components/shadcn-space/blocks/statistics-17/statistics17"

export default function Page() {
    return (
        <div>
            <Statistics17 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import { Card } from '@/components/ui/card'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics17 = () => {
  const optionSales: any = {
    labels: ['36%', '10%', '16%', '16%', '10%', '15%'],
    chart: {
      type: 'donut',
      fontFamily: 'inherit',
      foreColor: '#c6d1e9',
    },
    plotOptions: {
      pie: {
        startAngle: -90,
        endAngle: 90,
        offsetY: 10,
        donut: {
          size: '75%',
          labels: {
            show: true,
            name: {
              show: true,
              fontSize: '15px',
              offsetY: 5,
              color: '#0ea5e9',
            },
            value: {
              show: false,
            },
            total: {
              show: true,
              color: 'var(--color-foreground)',
              fontSize: '20px',
              fontWeight: '600',
              label: '8364',
            },
          },
        },
      },
    },
    grid: {
      padding: {
        bottom: -80,
      },
    },
    legend: {
      show: false,
    },
    dataLabels: {
      enabled: false,
      name: {
        show: false,
      },
    },
    stroke: {
      width: 0,
    },
    tooltip: {
      enabled: false,
      fillSeriesColor: false,
    },
    colors: ['var(--color-sky-400)', 'var(--color-blue-500)', 'var(--color-red-500)', 'var(--color-neutral-50)', 'var(--color-neutral-200)', 'var(--color-teal-400)'],
  }

  const salesChart = [30, 10, 16, 16, 16, 10]

  const dropdownItems = ['Action', 'Another action', 'Something else']

  const chartData = [
    {
      color: 'bg-sky-400',
      title: '36% Modernize',
    },
    {
      color: 'bg-teal-400',
      title: '17% Spike',
    },
    {
      color: 'bg-blue-500',
      title: '22% Ample',
    },
    {
      color: 'bg-red-500',
      title: '31% MaterialM',
    },
  ]

  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-md p-6 gap-3'>
          <div className='flex items-center justify-between '>
            <h5 className='text-lg font-semibold text-foreground'>
              Product Sales
            </h5>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent  hover:text-accent-foreground cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <div className='h-[220px]'>
            <Chart
              options={optionSales}
              series={salesChart}
              type='donut'
              height='260px'
              width='100%'
              className='mt-5'
            />
          </div>
          <div className='bg-teal-400/10 text-teal-400 mx-auto -mt-4 flex gap-1 items-center py-2 px-3 rounded-full w-fit justify-center text-xs font-medium'>
            <Icon icon='bi:lightning-charge-fill' />
            Best Seller
          </div>

          <div className='grid grid-cols-12 gap-2 mt-7 border-b border-border pb-3'>
            {chartData.map((item, index) => (
              <div className='sm:col-span-6  col-span-12' key={index}>
                <div className='flex gap-2 items-center'>
                  <span
                    className={`rounded-full w-[18px] h-[10px] ${item.color}`}></span>
                  <h6 className='text-sm font-normal text-foreground'>{item.title}</h6>
                </div>
              </div>
            ))}
          </div>
          <p className='text-center text-xs font-normal text-muted-foreground max-w-3xs mx-auto'>
            This is overview of the sales happened this month for the material
            website
          </p>
        </Card>
      </div>
    </>
  )
}

export default Statistics17
