import Statistics18 from "@/components/shadcn-space/blocks/statistics-18/statistics18"

export default function Page() {
    return (
        <div>
            <Statistics18 />
        </div>
    )
}

'use client'

import Link from 'next/link'
import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import { Card } from '@/components/ui/card'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics18 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']

  const MarketingData = [
    {
      icon: 'solar:course-up-line-duotone',
      title: '+2.9k',
      subtitle: 'Google Ads',
      color: 'bg-red-500/10 text-red-500',
    },
    {
      icon: 'solar:cursor-line-duotone',
      title: '1.22',
      subtitle: 'Referral',
      color: 'bg-teal-400/10 text-teal-400',
    },
    {
      icon: 'solar:pie-chart-3-line-duotone',
      title: '24.3K',
      subtitle: 'Oraganic',
      color: 'bg-sky-400/10 text-sky-400',
    },
  ]

  const ChartData: any = {
    color: '#adb5bd',
    series: [70, 18, 12],
    labels: ['24.3k', '1.22', '+2.9k'],
    chart: {
      height: 175,
      type: 'donut',
      color: '#adb5bd',
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
    },

    plotOptions: {
      pie: {
        startAngle: 0,
        endAngle: 360,
        donut: {
          size: '85%',
          labels: {
            show: true,
            name: {
              show: true,
              offsetY: 7,
            },
            value: {
              show: false,
            },
            total: {
              show: true,
              fontSize: '20px',
              fontWeight: '600',
              label: '24.3k',
            },
          },
        },
      },
    },
    stroke: {
      show: true,
      colors: 'var(--color-surface-ld)',
      width: 3,
    },
    dataLabels: {
      enabled: false,
    },

    legend: {
      show: false,
    },
    colors: [
      'var(--color-sky-400)',
      'var(--color-teal-400)',
      'var(--color-red-500)',
    ],

    tooltip: {
      theme: 'dark',
      fillSeriesColor: false,
    },
  }

  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-md p-6'>
          <div className='flex items-center justify-between'>
            <h5 className='text-lg font-semibold text-foreground'>Marketing Report</h5>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent  hover:text-accent-foreground cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <div className='grid grid-cols-12 gap-6 mt-3'>
            <div className='sm:col-span-6  col-span-12 '>
              <div className='flex sm:flex-col flex-row flex-wrap gap-6'>
                {MarketingData.map((item, index) => (
                  <div key={index} className='flex gap-3 items-center'>
                    <span
                      className={`w-10 h-10 rounded-full flex items-center justify-center ${item.color}`}>
                      <Icon icon={item.icon} height={24} />
                    </span>
                    <div>
                      <p className='text-sm font-normal text-muted-foreground'>{item.subtitle}</p>
                      <h4 className='text-xl font-semibold text-foreground'>{item.title}</h4>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className='sm:col-span-6 col-span-12'>
              <Chart
                options={ChartData}
                series={ChartData.series}
                type='donut'
                height='175px'
                width='100%'
              />
            </div>
          </div>

          <div className='bg-muted rounded-xl py-3 px-4 flex justify-between items-center mt-6'>
            <p className='text-sm font-normal text-muted-foreground'>Learn insigs how to manage</p>
            <Link
              href='/'
              className='h-10 w-10 flex items-center justify-center bg-sky-400 text-white rounded-full'>
              <Icon icon='solar:play-linear' height='18'></Icon>
            </Link>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics18
