import Statistics19 from "@/components/shadcn-space/blocks/statistics-19/statistics19"

export default function Page() {
    return (
        <div>
            <Statistics19 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics19 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']

  const ChartData: any = {
    series: [
      {
        name: 'Paypal',
        data: [29, 52, 38, 47, 56, 41, 46],
      },
      {
        name: 'Credit Card',
        data: [71, 71, 71, 71, 71, 71, 71],
      },
    ],

    chart: {
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
      type: 'bar',
      height: 150,
      stacked: true,
      offsetX: -5,
      toolbar: {
        show: false,
      },
    },
    grid: {
      show: false,
      borderColor: 'rgba(0,0,0,0.1)',
      strokeDashArray: 1,
      xaxis: {
        lines: {
          show: false,
        },
      },
      yaxis: {
        lines: {
          show: true,
        },
      },
      padding: {
        top: 0,
        right: 0,
        bottom: 0,
        left: 0,
      },
    },
    colors: ['var(--color-sky-400)', 'var(--color-neutral-100)'],
    plotOptions: {
      bar: {
        horizontal: false,
        columnWidth: '26%',
        borderRadius: [3],
        borderRadiusApplication: 'end',
        borderRadiusWhenStacked: 'all',
      },
    },
    dataLabels: {
      enabled: false,
    },
    xaxis: {
      categories: [['M'], ['T'], ['W'], ['T'], ['F'], ['S'], ['S']],
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      labels: {
        show: false,
      },
    },
    tooltip: {
      theme: 'dark',
    },
    legend: {
      show: false,
    },
  }
  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-xs p-6'>
          <div className='flex items-center justify-between'>
            <h5 className='text-lg font-semibold text-foreground'>Payments</h5>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent  hover:text-accent-foreground cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <div>
            <h6 className='text-xl font-semibold text-foreground flex items-center'>
              12,389
              <Badge className='ms-2 bg-red-500/10 text-red-500'>-3.8%</Badge>
            </h6>
            <p className='text-sm text-muted-foreground'>Last 7 days</p>
          </div>
          <div className='rounded-bars -ms-3 -me-2 h-[150px]'>
            <Chart
              options={ChartData}
              series={ChartData.series}
              type='bar'
              height='150px'
              width='100%'
            />
          </div>

          <div className='flex flex-col gap-2'>
            <div className='flex justify-between align-center '>
              <div className='flex items-center gap-2'>
                <span className='bg-sky-400 rounded-full w-5 h-2.5'></span>
                <p className='text-sm font-normal text-muted-foreground'>Paypal</p>
              </div>
              <p className='text-sm font-normal text-muted-foreground'>52%</p>
            </div>
            <div className='flex justify-between align-center'>
              <div className='flex items-center gap-2'>
                <span className='bg-stone-200 dark:bg-stone-600 rounded-full w-5 h-2.5'></span>
                <p className='text-sm font-normal text-muted-foreground'>Credit Card</p>
              </div>
              <p className='text-sm font-normal text-muted-foreground'>48%</p>
            </div>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics19
