import Statistics21 from "@/components/shadcn-space/blocks/statistics-21/statistics21"

export default function Page() {
    return (
        <div>
            <Statistics21 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { Card } from '@/components/ui/card'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics21 = () => {
  const dropdownItems = ['This Week', 'April 2025', 'May 2025', 'June 2025']
  const CongratsData = [
    {
      icon: 'solar:cart-4-line-duotone',
      title: '64 new orders',
      subtitle: 'Processing',
      color: 'bg-sky-400/10 text-sky-400',
    },
    {
      icon: 'solar:checklist-minimalistic-line-duotone',
      title: '4 orders',
      subtitle: 'On hold',
      color: 'bg-red-500/10 text-red-500',
    },
    {
      icon: 'solar:banknote-line-duotone',
      title: '12 orders',
      subtitle: 'Delivered',
      color: 'bg-teal-400/10 text-teal-400',
    },
  ]

  const ChartData: any = {
    series: [
      {
        name: '',
        data: [0, 20, 15, 19, 14, 25, 30],
      },
      {
        name: '',
        data: [0, 8, 19, 13, 26, 16, 25],
      },
    ],

    chart: {
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
      height: 250,
      width: '100%',
      type: 'line',
      offsetX: -15,
      toolbar: {
        show: false,
      },
      stacked: false,
    },
    legend: {
      show: false,
    },
    stroke: {
      width: 3,
      curve: 'smooth',
    },
    grid: {
      borderColor: 'rgba(173,181,189,0.3)',
      strokeDashArray: 3,
      xaxis: {
        lines: {
          show: true,
        },
      },
      yaxis: {
        lines: {
          show: true,
        },
      },
      padding: {
        top: 0,
        bottom: 0,
        left: 10,
        right: 0,
      },
    },
    colors: ['var(--color-sky-400)', 'var(--color-blue-500)'],
    fill: {
      type: 'gradient',
      gradient: {
        shade: 'dark',
        gradientToColors: ['#6993ff'],
        shadeIntensity: 1,
        type: 'horizontal',
        opacityFrom: 1,
        opacityTo: 1,
        stops: [0, 100, 100, 100],
      },
    },
    markers: {
      size: 0,
    },
    xaxis: {
      labels: {
        show: true,
      },
      type: 'category',
      categories: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
      axisTicks: {
        show: false,
      },
      axisBorder: {
        show: false,
      },
    },
    yaxis: {
      axisTicks: {
        show: false,
      },
      axisBorder: {
        show: false,
      },
      labels: {
        show: true,
        formatter: function (value: string) {
          return value + 'k'
        },
      },

      min: 0,
      max: 32,
      tickAmount: 4,
    },
    tooltip: {
      theme: 'dark',
    },
  }

  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-6xl p-6'>
          <div className='grid grid-cols-12 gap-6'>
            <div className='lg:col-span-6 col-span-12'>
              <h5 className='text-lg font-semibold text-foreground'>
                Congratulations Jonathan
              </h5>
              <p className='text-sm font-medium text-muted-foreground'>
                You have done 38% more sales
              </p>
              <div className='grid grid-cols-12 gap-6 mt-3 items-center'>
                <div className='md:col-span-5 col-span-12'>
                  <div className='flex sm:flex-col flex-row flex-wrap gap-4'>
                    {CongratsData.map((item, index) => (
                      <div key={index} className='flex gap-3 items-center'>
                        <span
                          className={`w-10 h-10 rounded-full flex items-center justify-center ${item.color}`}>
                          <Icon icon={item.icon} height={24} />
                        </span>
                        <div>
                          <h4 className='text-base font-semibold text-foreground'>
                            {item.title}
                          </h4>
                          <p className='text-sm font-normal text-muted-foreground'>
                            {item.subtitle}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
                <div className='md:col-span-7 col-span-12 text-end'>
                  <img
                    src={
                      'https://images.shadcnspace.com/assets/backgrounds/teamwork.png'
                    }
                    alt='background'
                    width={300}
                    height={226}
                  />
                </div>
              </div>
            </div>
            <div className='lg:col-span-6 col-span-12'>
              <div className='flex items-center justify-between'>
                <div>
                  <h5 className='text-lg font-semibold text-foreground'>
                    Total Orders
                  </h5>
                  <p className='text-sm font-medium text-muted-foreground'>
                    Weekly order updates
                  </p>
                </div>

                <Select>
                  <SelectTrigger className='w-[150px] focus-visible:ring-0 rounded-full cursor-pointer'>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className='p-0'>
                    {dropdownItems.map((item, index) => (
                      <SelectItem
                        key={index}
                        value={item}
                        className={`py-1.5 px-3`}>
                        {item}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className='-me-4 mt-3 h-[250px]'>
                <Chart
                  options={ChartData}
                  series={ChartData.series}
                  type='line'
                  height='250px'
                  width='100%'
                />
              </div>
            </div>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics21
