import Statistics23 from "@/components/shadcn-space/blocks/statistics-23/statistics23"

export default function Page() {
    return (
        <div>
            <Statistics23 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics23 = () => {
  const ChartData: any = {
    color: '#adb5bd',
    series: [70, 18, 12],
    labels: ['2024', '2023', '2022'],
    chart: {
      height: 175,
      type: 'donut',
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
    },
    plotOptions: {
      pie: {
        startAngle: 0,
        endAngle: 360,
        donut: {
          size: '85%',
        },
      },
    },
    stroke: {
      show: true,
      colors: ['transparent'],
      width: 3,
    },
    dataLabels: {
      enabled: false,
    },
    legend: {
      show: false,
    },
    colors: ['var(--color-sky-400)', 'var(--color-teal-400)', 'var(--color-red-500)'],
    tooltip: {
      theme: 'dark',
      fillSeriesColor: false,
    },
  }
  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-xs p-6 gap-4'>
          <div className='flex items-baseline  justify-between'>
            <div>
              <h5 className='text-lg font-semibold text-foreground'>
                Products
              </h5>
              <p className='text-sm font-medium text-muted-foreground'>
                Last 7 days
              </p>
            </div>
            <div className='text-end'>
              <h5 className='text-base font-semibold text-foreground'>
                12,389
              </h5>
              <Badge className='bg-teal-400/10 text-teal-400 tracking-wide'>
                +26.5%
              </Badge>
            </div>
          </div>
          <div className='h-[175px]'>
            <Chart
              options={ChartData}
              series={ChartData.series}
              type='donut'
              height='175px'
              width='100%'
              className='my-4'
            />
          </div>
          <p className='text-xs font-normal text-center text-muted-foreground'>
            Profit more than last month
          </p>
        </Card>
      </div>
    </>
  )
}

export default Statistics23
