import Statistics24 from "@/components/shadcn-space/blocks/statistics-24/statistics24"

export default function Page() {
    return (
        <div>
            <Statistics24 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { Badge } from '@/components/ui/badge'
import { Card } from '@/components/ui/card'
import { Tabs, TabsList, TabsTrigger, TabsContent } from '@/components/ui/tabs'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics24 = () => {
  const IconData = [
    {
      icon: 'solar:course-up-line-duotone',
      title: '$14,673',
      subtitle: 'Total Sales',
      color: 'bg-amber-300/10 text-amber-300',
    },
    {
      icon: 'solar:dollar-minimalistic-line-duotone',
      title: '$9,281',
      subtitle: 'Total Profit',
      color: 'bg-red-500/10 text-red-500',
    },
    {
      icon: 'solar:flag-2-line-duotone',
      title: '45.1k',
      subtitle: 'Total Users',
      color: 'bg-teal-400/10 text-teal-400',
    },
    {
      icon: 'solar:pie-chart-line-duotone',
      title: '$4,673',
      subtitle: 'Total Expense',
      color: 'bg-sky-400/10 text-sky-400',
    },
  ]

  const ChartData1: any = {
    series: [
      {
        name: 'BTC',
        data: [3500, 2500, 4000, 2500, 5500, 3500, 2500],
      },
      {
        name: 'ETH',
        data: [3000, 1500, 3100, 5000, 3000, 5500, 3500],
      },
    ],
    chart: {
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
      type: 'line',
      toolbar: {
        show: false,
      },
    },
    legend: {
      show: false,
    },
    stroke: {
      width: 3,
      curve: 'smooth',
    },
    grid: {
      show: false,
      strokeDashArray: 3,
      borderColor: '#90A4AE50',
    },
    colors: ['var(--color-sky-400)', 'var(--color-neutral-100)'],
    markers: {
      size: 0,
    },
    yaxis: {
      show: false,
    },
    xaxis: {
      type: 'category',
      categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'July'],
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    tooltip: {
      theme: 'dark',
    },
  }

  const AreaChartData: any = {
    series: [
      {
        type: 'area',
        name: 'This Year',
        data: [
          {
            x: 'Aug',
            y: 25,
          },
          {
            x: 'Sep',
            y: 13,
          },
          {
            x: 'Oct',
            y: 20,
          },
          {
            x: 'Nov',
            y: 40,
          },
          {
            x: 'Dec',
            y: 45,
          },
          {
            x: 'Jan',
            y: 50,
          },
          {
            x: 'Feb',
            y: 70,
          },
          {
            x: 'Mar',
            y: 30,
          },
        ],
      },
      {
        type: 'line',
        name: 'Last Year',
        chart: {
          foreColor: '#adb0bb',
          dropShadow: {
            enabled: true,
            enabledOnSeries: undefined,
            top: 5,
            left: 0,
            blur: 3,
            color: '#000',
            opacity: 0.1,
          },
        },
        data: [
          {
            x: 'Aug',
            y: 50,
          },
          {
            x: 'Sep',
            y: 35,
          },
          {
            x: 'Oct',
            y: 30,
          },
          {
            x: 'Nov',
            y: 20,
          },
          {
            x: 'Dec',
            y: 20,
          },
          {
            x: 'Jan',
            y: 30,
          },
          {
            x: 'Feb',
            y: 35,
          },
          {
            x: 'Mar',
            y: 40,
          },
        ],
      },
    ],
    chart: {
      height: 210,
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
      offsetX: 0,
      animations: {
        speed: 500,
      },
      toolbar: {
        show: false,
      },
    },
    colors: ['#0ea5e9', 'rgba(119, 119, 142, 0.05)'],
    dataLabels: {
      enabled: false,
    },
    fill: {
      type: 'gradient',
      gradient: {
        shadeIntensity: 0,
        inverseColors: false,
        opacityFrom: 0.3,
        opacityTo: 0,
        stops: [100],
      },
    },
    grid: {
      borderColor: '#90A4AE30',
    },
    stroke: {
      curve: 'smooth',
      width: 2,
    },
    xaxis: {
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      min: 0,
      max: 80,
      tickAmount: 4,
    },
    legend: {
      show: false,
    },
    tooltip: {
      theme: 'dark',
    },
  }

  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-3xl p-6'>
          <div className='grid grid-cols-12 gap-7'>
            <div className='md:col-span-8 col-span-12'>
              <Tabs defaultValue='Orders' className='w-full'>
                <div className='sm:flex items-start justify-between'>
                  <div>
                    <p className='text-sm font-light text-foreground'>
                      Overall Balance
                    </p>
                    <h4 className='text-3xl font-semibold text-foreground my-1'>
                      $2,538,942
                    </h4>
                    <div className='flex gap-1 items-center'>
                      <Badge className='py-1 px-2.5 gap-1 bg-teal-400/10 text-teal-400'>
                        <Icon
                          icon='solar:alt-arrow-down-bold'
                          className='me-1'
                          height={17}
                        />
                        16.3%
                      </Badge>
                      <small className='text-xs text-muted-foreground'>
                        last 12 months
                      </small>
                    </div>
                  </div>
                  <div className='mb-4 sm:mt-0 mt-6'>
                    <TabsList className='flex flex-wrap bg-muted rounded-full w-fit'>
                      <TabsTrigger
                        value='Orders'
                        className='py-2 px-4 rounded-full min-w-[100px] text-xs font-semibold data-[state=active]:bg-background dark:data-[state=active]:bg-dark data-[state=active]:text-foreground
             data-[state=inactive]:text-muted-foreground cursor-pointer'>
                        Orders
                      </TabsTrigger>

                      <TabsTrigger
                        value='Expenses'
                        className='py-2 px-4 rounded-full min-w-[100px] text-xs font-semibold data-[state=active]:bg-background dark:data-[state=active]:bg-dark data-[state=active]:text-foreground
             data-[state=inactive]:text-muted-foreground cursor-pointer'>
                        Expenses
                      </TabsTrigger>
                    </TabsList>
                  </div>
                </div>

                <TabsContent value='Orders'>
                  <Chart
                    options={ChartData1}
                    series={ChartData1.series}
                    type='line'
                    height='210px'
                    width='100%'
                    className='mt-4'
                  />
                </TabsContent>

                <TabsContent value='Expenses'>
                  <div className='mt-4 -ms-3 -me-4'>
                    <Chart
                      options={AreaChartData}
                      series={AreaChartData.series}
                      type='area'
                      height='210px'
                      width='100%'
                    />
                  </div>
                </TabsContent>
              </Tabs>
            </div>
            <div className='md:col-span-4 col-span-12 pb-5'>
              <div className='flex flex-col gap-2'>
                {IconData.map((item, index) => (
                  <div
                    key={index}
                    className='flex gap-3 items-center rounded-xl bg-accent px-4 py-5'>
                    <span
                      className={`w-10 h-10 rounded-full flex items-center justify-center ${item.color}`}>
                      <Icon icon={item.icon} height={24} />
                    </span>
                    <div>
                      <p className='text-xs font-medium text-muted-foreground'>
                        {item.subtitle}
                      </p>
                      <h5 className='text-sm font-semibold text-foreground'>{item.title}</h5>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics24
