import Statistics25 from "@/components/shadcn-space/blocks/statistics-25/statistics25"

export default function Page() {
    return (
        <div>
            <Statistics25 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Card } from '@/components/ui/card'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
} from '@/components/ui/dropdown-menu'


const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics25 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']
  const ChartData: any = {
    series: [
      {
        name: '',
        data: [128, 193, 150, 120, 174, 150],
      },
    ],
    chart: {
      toolbar: {
        show: false,
      },
      height: 230,
      type: 'bar',
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
    },
    colors: [
      'rgba(0,0,0,0.1)',
      '#38BDF8',
      'rgba(0,0,0,0.1)',
      'rgba(0,0,0,0.1)',
      'rgba(0,0,0,0.1)',
      'rgba(0,0,0,0.1)',
    ],
    plotOptions: {
      bar: {
        borderRadius: 12,
        columnWidth: '59%',
        distributed: true,
        endingShape: 'rounded',
      },
    },

    dataLabels: {
      enabled: false,
    },
    legend: {
      show: false,
    },
    grid: {
      yaxis: {
        lines: {
          show: false,
        },
      },
      xaxis: {
        lines: {
          show: false,
        },
      },
    },
    xaxis: {
      categories: [['JAN'], ['FEB'], ['MAR'], ['APR'], ['MAY'], ['JUN']],
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      labels: {
        show: false,
      },
    },
    tooltip: {
      theme: 'dark',
    },
  }
  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='bg-sky-400/10 w-full max-w-sm p-6 border-none'>
          <div className='flex items-center justify-between'>
            <h5 className='text-lg font-semibold text-foreground'>Return On Investment</h5>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <div className='flex items-center pt-4'>
            <span className='w-10 h-10 rounded-full flex items-center justify-center bg-white dark:bg-black/20'>
              <Icon icon='solar:course-up-line-duotone' height={24} />
            </span>
            <div className='flex items-center'>
              <div className='px-6'>
                <h4 className='text-xl font-semibold text-foreground'>283%</h4>
                <p className='text-sm font-normal text-muted-foreground'>ROI</p>
              </div>
              <div className='px-6 border-s border-border'>
                <h4 className='text-lg font-semibold text-sky-400'>+24%</h4>
                <p className='text-sm font-normal text-muted-foreground'>January</p>
              </div>
            </div>
          </div>
          <div className='-ms-3 h-[230px]'>
            <Chart
              options={ChartData}
              series={ChartData.series}
              type='bar'
              height='230px'
              width='100%'
            />
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics25
