import Statistics26 from "@/components/shadcn-space/blocks/statistics-26/statistics26"

export default function Page() {
    return (
        <div>
            <Statistics26 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Card } from '@/components/ui/card'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
} from '@/components/ui/dropdown-menu'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics26 = () => {
  const Action = [
    {
      icon: 'solar:add-circle-outline',
      listtitle: 'Add',
    },
    {
      icon: 'solar:pen-new-square-broken',
      listtitle: 'Edit',
    },
    {
      icon: 'solar:trash-bin-minimalistic-outline',
      listtitle: 'Delete',
    },
  ]

  const ChartData: any = {
    series: [
      {
        name: '1',
        data: [29, 52, 38, 47, 56],
      },
      {
        name: '2',
        data: [71, 71, 71, 71, 71],
      },
    ],
    chart: {
      fontFamily: 'inherit',
      type: 'bar',
      height: 100,
      stacked: true,
      toolbar: {
        show: false,
      },
      sparkline: {
        enabled: true,
      },
    },
    grid: {
      show: false,
      borderColor: 'rgba(0,0,0,0.1)',
      strokeDashArray: 1,
      xaxis: {
        lines: {
          show: false,
        },
      },
      yaxis: {
        lines: {
          show: true,
        },
      },
      padding: {
        top: 0,
        right: 0,
        bottom: 0,
        left: 0,
      },
    },
    colors: ['#f43f5e', 'rgba(17, 28, 45, 0.30)'],
    plotOptions: {
      bar: {
        horizontal: false,
        columnWidth: '30%',
        borderRadius: [3],
        borderRadiusApplication: 'end',
        borderRadiusWhenStacked: 'all',
      },
    },
    dataLabels: {
      enabled: false,
    },
    xaxis: {
      labels: {
        show: false,
      },
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      labels: {
        show: false,
      },
    },
    tooltip: {
      theme: 'dark',
    },
    legend: {
      show: false,
    },
  }
  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-sm p-6 gap-8 bg-red-500/10 border-none'>
          <div className='flex items-center justify-between'>
            <div className='flex items-center gap-3'>
              <span className='w-10 h-10 rounded-full flex items-center justify-center bg-red-500 text-white'>
                <Icon
                  icon='solar:users-group-rounded-bold-duotone'
                  height={24}
                />
              </span>
              <h5 className='text-base font-semibold text-foreground/70'>Total followers</h5>
            </div>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger >
                  <span className='h-9 w-9 flex justify-center items-center rounded-full cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent className=' p-1'>
                  {Action.map((items, index) => (
                    <DropdownMenuItem key={index} className='flex gap-3 font-normal cursor-pointer'>
                      <Icon icon={`${items.icon}`} height={18} className='text-inherit' />
                      <span>{items.listtitle}</span>
                    </DropdownMenuItem>
                  ))}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <div className='grid grid-cols-12 gap-[24px] items-end'>
            <div className='xl:col-span-6 col-span-7'>
              <h4 className='text-3xl font-semibold text-foreground mb-3'>4,562</h4>
              <span className='font-semibold border rounded-full border-border py-0.5 px-[10px] leading-[normal] text-xs text-foreground/70'>
                +23% last month
              </span>
            </div>
            <div className='xl:col-span-6  col-span-5 '>
              <div className='rounded-bars md:ps-7 h-[100px]'>
                <Chart
                  options={ChartData}
                  series={ChartData.series}
                  type='bar'
                  height='100px'
                  width='100%'
                />
              </div>
            </div>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics26
