import Statistics27 from "@/components/shadcn-space/blocks/statistics-27/statistics27"

export default function Page() {
    return (
        <div>
            <Statistics27 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
} from '@/components/ui/dropdown-menu'
import { Card } from '@/components/ui/card'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics27 = () => {
  const Action = [
    {
      icon: 'solar:add-circle-outline',
      listtitle: 'Add',
    },
    {
      icon: 'solar:pen-new-square-broken',
      listtitle: 'Edit',
    },
    {
      icon: 'solar:trash-bin-minimalistic-outline',
      listtitle: 'Delete',
    },
  ]

  const ChartData: any = {
    series: [
      {
        name: 'monthly earnings',
        color: 'var(--color-blue-500)',
        data: [25, 66, 20, 40, 12, 58, 20],
      },
    ],
    chart: {
      id: 'total-income',
      type: 'area',
      height: 70,
      sparkline: {
        enabled: true,
      },
      group: 'sparklines',
      fontFamily: 'inherit',
      foreColor: 'var(--color-blue-500)',
    },
    stroke: {
      curve: 'smooth',
      width: 2,
    },
    fill: {
      type: 'gradient',
      gradient: {
        shadeIntensity: 0,
        inverseColors: false,
        opacityFrom: 0,
        opacityTo: 0,
        stops: [20, 180],
      },
    },

    markers: {
      size: 0,
    },
    tooltip: {
      theme: 'dark',
      x: {
        show: false,
      },
    },
  }
  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-sm p-6 gap-8 bg-blue-500/10 dark:bg-blue-500/12 border-none'>
          <div className='flex items-center justify-between'>
            <div className='flex items-center gap-3'>
              <span className='w-10 h-10 rounded-full flex items-center justify-center bg-blue-500 text-white'>
                <Icon icon='solar:wallet-2-line-duotone' height={24} />
              </span>
              <h5 className='text-base font-semibold text-foreground/70'>Total Income</h5>
            </div>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger >
                  <span className='h-9 w-9 flex justify-center items-center rounded-full cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent className=' p-1'>
                  {Action.map((items, index) => (
                    <DropdownMenuItem key={index} className='flex gap-3 font-normal cursor-pointer'>
                      <Icon icon={`${items.icon}`} height={18} className='text-inherit' />
                      <span>{items.listtitle}</span>
                    </DropdownMenuItem>
                  ))}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <div className='grid grid-cols-12 gap-[24px] items-end'>
            <div className='xl:col-span-6 col-span-7'>
              <h4 className='text-3xl font-semibold text-foreground mb-3'>$6,280</h4>
              <span className='font-semibold border rounded-full border-border py-0.5 px-[10px] leading-[normal] text-xs text-foreground/70'>
                +18% last month
              </span>
            </div>
            <div className='xl:col-span-6 col-span-5'>
              <div className='rounded-bars md:ps-7 h-[70px]'>
                <Chart
                  options={ChartData}
                  series={ChartData.series}
                  type='area'
                  height='70px'
                  width='100%'
                />
              </div>
            </div>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics27
