import Statistics29 from "@/components/shadcn-space/blocks/statistics-29/statistics29"

export default function Page() {
    return (
        <div>
            <Statistics29 />
        </div>
    )
}

'use client'

import Link from 'next/link'
import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
} from '@/components/ui/dropdown-menu'
import { Card } from '@/components/ui/card'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics29 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']

  const MarketingData = [
    {
      icon: 'solar:course-up-line-duotone',
      title: '+2.9k',
      subtitle: 'Search Volume',
      color: 'bg-red-500/10 text-red-500',
    },
    {
      icon: 'solar:pie-chart-3-line-duotone',
      title: '1.22',
      subtitle: 'Return Ratio',
      color: 'bg-amber-300/10 text-amber-300',
    },
    {
      icon: 'solar:cursor-line-duotone',
      title: '0.83',
      subtitle: 'Clicks / search',
      color: 'bg-teal-400/10 text-teal-400',
    },
    {
      icon: 'solar:pie-chart-3-line-duotone',
      title: '41.2',
      subtitle: 'Return Ratio',
      color: 'bg-sky-400/10 text-sky-400',
    },
  ]

  const ChartData: any = {
    series: [20, 20, 20, 20, 20],
    labels: ['245', '45', '14', '78', '95'],
    chart: {
      height: 200,
      fontFamily: 'inherit',
      type: 'donut',
    },
    plotOptions: {
      pie: {
        startAngle: -90,
        endAngle: 90,
        offsetY: 10,
        donut: {
          size: '85%',
        },
      },
    },
    grid: {
      padding: {
        bottom: -80,
      },
    },
    legend: {
      show: false,
    },
    dataLabels: {
      enabled: false,
      name: {
        show: false,
      },
    },
    stroke: {
      width: 2,
      colors: ['transparent'],
    },
    tooltip: {
      fillSeriesColor: false,
    },
    colors: [
      'var(--color-red-500)',
      'var(--color-amber-300)',
      'var(--color-amber-100)',
      'var(--color-teal-100)',
      'var(--color-teal-400)',
    ],
  }

  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-lg p-6'>
          <div className='flex items-center justify-between'>
            <h5 className='text-lg font-semibold text-foreground'>Marketing Report</h5>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent hover:text-accent-foreground cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <div className='grid grid-cols-12 gap-6 mt-5 items-center'>
            <div className='sm:col-span-6  col-span-12 '>
              <div className='flex flex-col gap-5'>
                {MarketingData.map((item, index) => (
                  <div key={index} className='flex gap-3 items-center'>
                    <span
                      className={`w-10 h-10 rounded-full flex items-center justify-center ${item.color}`}>
                      <Icon icon={item.icon} height={24} />
                    </span>
                    <div>
                      <p className='text-sm font-normal text-muted-foreground'>{item.subtitle}</p>
                      <h4 className='text-xl font-semibold text-foreground'>{item.title}</h4>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className='sm:col-span-6 col-span-12'>
              <div className='text-center h-[200px]'>
                <Chart
                  options={ChartData}
                  series={ChartData.series}
                  type='donut'
                  height='200px'
                  width='100%'
                />
                <h4 className='text-3xl font-semibold text-foreground/75'>275</h4>
                <p className='mt-3 text-sm font-normal text-muted-foreground'>
                  Learn insigs how to manage all aspects of your startup.
                </p>
              </div>
            </div>
          </div>

          <div className='bg-muted rounded-xl py-[18px] px-6 flex justify-between items-center mt-6'>
            <div className='w-3/4 flex gap-3 items-center'>
              <Icon
                icon='solar:plain-2-line-duotone'
                height={40}
                className='text-sky-400'
              />
              <p className='text-sm font-normal text-muted-foreground'>
                Learn insigs how to manage all aspects of your startup.
              </p>
            </div>

            <Link
              href='/'
              className='h-10 w-10 flex items-center justify-center bg-sky-400 text-white rounded-full'>
              <Icon icon='solar:play-linear' height='18'></Icon>
            </Link>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics29
