import Statistics30 from "@/components/shadcn-space/blocks/statistics-30/statistics30"

export default function Page() {
    return (
        <div>
            <Statistics30 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Card } from '@/components/ui/card'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuItem,
  DropdownMenuContent,
} from '@/components/ui/dropdown-menu'
import { Tabs, TabsList, TabsTrigger, TabsContent } from '@/components/ui/tabs'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics30 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']

  const ChartData: any = {
    chart: {
      fontFamily: 'inherit',
      type: 'radar',
      height: 350,
      toolbar: { show: !1 },
      foreColor: '#adb0bb',
    },
    series: [
      { name: 'Sales', data: [32, 27, 27, 30, 25, 25] },
      { name: 'Visits', data: [25, 35, 20, 20, 20, 20] },
    ],
    plotOptions: {
      radar: {
        polygons: {
          strokeColors: '#90A4AE50',
          connectorColors: '#90A4AE50',
        },
      },
    },
    colors: ['var(--color-sky-400)', 'var(--color-red-100)'],
    legend: {
      show: false,
    },
    fill: {
      colors: ['var(--color-sky-400)', 'var(--color-red-100)'],
      opacity: [1, 1],
    },
    markers: { size: 0 },
    grid: {
      show: false,
    },
    xaxis: {
      categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      labels: {
        show: !0,
        style: {
          fontSize: '13px',
        },
      },
    },
    yaxis: { show: !1, min: 0, max: 40, tickAmount: 4 },
    tooltip: {
      theme: 'dark',
    },
  }

  const optionexpenses: any = {
    series: [
      {
        name: 'This year',
        data: [9, 5, 3, 7, 5, 10, 3],
      },
      {
        name: 'Last year ',
        data: [6, 3, 9, 5, 4, 6, 4],
      },
    ],

    chart: {
      fontFamily: 'inherit',
      type: 'bar',
      height: 350,
      offsetY: 10,
      offsetX: -18,
      toolbar: {
        show: false,
      },
    },
    grid: {
      show: true,
      strokeDashArray: 3,
      borderColor: 'rgba(0,0,0,.1)',
    },
    colors: ['#0ea5e9', '#8b5cf6'],
    plotOptions: {
      bar: {
        borderRadius: 5,
        horizontal: false,
        columnWidth: '30%',
        endingShape: 'rounded',
      },
    },
    dataLabels: {
      enabled: false,
    },
    stroke: {
      show: true,
      width: 5,
      colors: ['transparent'],
    },
    xaxis: {
      type: 'category',
      categories: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],

      axisTicks: {
        show: false,
      },
      axisBorder: {
        show: false,
      },
      labels: {
        style: {
          colors: '#a1aab2',
        },
      },
    },
    yaxis: {
      labels: {
        style: {
          colors: '#a1aab2',
        },
      },
      min: 0,
      max: 12,
      tickAmount: 4,
    },

    fill: {
      opacity: 1,
      colors: ['#38BDF8', '#3B82F6'],
    },
    tooltip: {
      theme: 'dark',
    },
    legend: {
      show: false,
    },
  }

  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-3xl p-6'>
          <Tabs defaultValue='Email' className='w-full'>
            <div className='sm:flex items-center justify-between'>
              <h5 className='text-lg font-semibold text-foreground'>
                Delivery Analytics
              </h5>
              <div className='flex items-center gap-3 sm:mt-0 mt-4 justify-between'>
                <TabsList className='flex flex-wrap bg-muted rounded-full w-fit'>
                  <TabsTrigger
                    value='Email'
                    className='py-2 px-4 rounded-full min-w-[100px] text-xs font-semibold data-[state=active]:bg-background dark:data-[state=active]:bg-dark data-[state=active]:text-foreground
             data-[state=inactive]:text-muted-foreground cursor-pointer'>
                    Email
                  </TabsTrigger>

                  <TabsTrigger
                    value='From Last Week'
                    className='py-2 px-4 rounded-full min-w-[100px] text-xs font-semibold data-[state=active]:bg-background dark:data-[state=active]:bg-dark data-[state=active]:text-foreground
             data-[state=inactive]:text-muted-foreground cursor-pointer'>
                    From Last Week
                  </TabsTrigger>
                </TabsList>
                <DropdownMenu>
                  <DropdownMenuTrigger className='outline-none'>
                    <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent hover:text-accent-foreground cursor-pointer'>
                      <HiOutlineDotsVertical size={22} />
                    </span>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent>
                    {dropdownItems.map((items, index) => {
                      return (
                        <DropdownMenuItem
                          key={index}
                          className='font-normal cursor-pointer'>
                          {items}
                        </DropdownMenuItem>
                      )
                    })}
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            </div>

            <TabsContent value='Email'>
              <div className='grid grid-cols-12 gap-7 items-center mt-4'>
                <div className='lg:col-span-7 col-span-12 h-[350px]'>
                  <Chart
                    options={ChartData}
                    series={ChartData.series}
                    type='radar'
                    height='350px'
                    width='100%'
                  />
                </div>
                <div className='lg:col-span-5 col-span-12'>
                  <div className='bg-muted dark:bg-darkmuted rounded-md px-5 py-4'>
                    <Icon
                      icon='material-symbols:rocket-launch-outline'
                      className='text-sky-400 mb-2'
                      height='30'></Icon>
                    <h5 className='text-lg font-semibold text-foreground my-1'>
                      Welcome Emails!
                    </h5>
                    <p className='text-sm font-medium text-muted-foreground'>
                      Have great potential!
                    </p>
                  </div>
                  <div className='flex gap-3 items-center mt-6'>
                    <span className='rounded-full w-[18px] h-[10px] bg-sky-400'></span>
                    <h6 className='text-base font-semibold text-foreground'>
                      22.3%
                    </h6>
                    <p className='text-sm font-medium text-foreground/90'>
                      Open Rate
                    </p>
                  </div>
                  <div className='flex gap-3 items-center mt-4'>
                    <span className='rounded-full w-[18px] h-[10px] bg-blue-500'></span>
                    <h6 className='text-base font-semibold text-foreground'>
                      14.2%
                    </h6>
                    <p className='text-sm font-medium text-foreground/90'>
                      Click-through Rate
                    </p>
                  </div>
                  <div className='flex gap-3 items-center mt-4'>
                    <span className='rounded-full w-[18px] h-[10px] bg-red-500'></span>
                    <h6 className='text-base font-semibold text-foreground'>
                      11.6%
                    </h6>
                    <p className='text-sm font-medium text-foreground/90'>
                      Conversation Rate
                    </p>
                  </div>
                </div>
              </div>
            </TabsContent>

            <TabsContent value='From Last Week'>
              <div className='-me-6 h-[350px]'>
                <Chart
                  options={optionexpenses}
                  series={optionexpenses.series}
                  type='bar'
                  height='350px'
                  width='100%'
                />
              </div>
            </TabsContent>
          </Tabs>
        </Card>
      </div>
    </>
  )
}

export default Statistics30
