import Statistics31 from "@/components/shadcn-space/blocks/statistics-31/statistics31"

export default function Page() {
    return (
        <div>
            <Statistics31 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Icon } from '@iconify/react'
import "./styles/style.css";

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const ChartData: any = {
  series: [
    {
      name: 'Paypal',
      data: [29, 52, 38, 47, 56, 41, 46],
    },
    {
      name: 'Amazone',
      data: [71, 71, 71, 71, 71, 71, 71],
    },
  ],

  chart: {
    fontFamily: 'inherit',
    foreColor: '#adb0bb',
    type: 'bar',
    height: 150,
    stacked: true,
    offsetX: -5,
    toolbar: {
      show: false,
    },
  },
  grid: {
    show: false,
    borderColor: 'rgba(0,0,0,0.1)',
    strokeDashArray: 1,
    xaxis: {
      lines: {
        show: false,
      },
    },
    yaxis: {
      lines: {
        show: true,
      },
    },
    padding: {
      top: 0,
      right: 0,
      bottom: 0,
      left: 0,
    },
  },
  colors: ['var(--color-sky-400)', 'var(--color-neutral-100)'],
  plotOptions: {
    bar: {
      horizontal: false,
      columnWidth: '26%',
      borderRadius: [3],
      borderRadiusApplication: 'end',
      borderRadiusWhenStacked: 'all',
    },
  },
  dataLabels: {
    enabled: false,
  },
  xaxis: {
    categories: [['M'], ['T'], ['W'], ['T'], ['F'], ['S'], ['S']],
    axisBorder: {
      show: false,
    },
    axisTicks: {
      show: false,
    },
  },
  yaxis: {
    labels: {
      show: false,
    },
  },
  tooltip: {
    theme: 'dark',
  },
  legend: {
    show: false,
  },
}

const Statistics31 = () => {
  return (
    <div className='min-h-screen flex flex-col items-center justify-center'>
      <Card className='w-full max-w-2xs p-6'>
        <div className='flex justify-between align-baseline'>
          <div>
            <h5 className='text-lg font-semibold text-foreground'>Earning</h5>
            <p className='text-sm font-medium text-muted-foreground'>Last 7 days</p>
          </div>
          <div>
            <h5 className='text-lg font-semibold text-foreground'>12,389</h5>
            <Badge className='bg-amber-300/10 text-amber-300 border-amber-300'>-3.8%</Badge>
          </div>
        </div>

        <div className='mt-3 -ms-4 h-[150px] rounded-bars'>
          <Chart
            options={ChartData}
            series={ChartData.series}
            type='bar'
            height='150px'
            width='100%'
          />
        </div>
        <h6 className='flex justify-between text-sm font-normal text-muted-foreground'>
          <span className='flex gap-3 items-center'>
            <Icon icon={'tabler:circle'} width={16} height={16} className='text-sky-400' /> Paypal
          </span>
          52%
        </h6>
        <h6 className='flex justify-between text-sm font-normal text-muted-foreground'>
          <span className='flex gap-3 items-center'>
            <Icon icon={'tabler:circle'} width={16} height={16} /> Amazon
          </span>
          48%
        </h6>
      </Card>
    </div>
  )
}

export default Statistics31
