import Statistics32 from "@/components/shadcn-space/blocks/statistics-32/statistics32"

export default function Page() {
    return (
        <div>
            <Statistics32 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Icon } from '@iconify/react'
import { Card } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuItem,
  DropdownMenuContent,
} from '@/components/ui/dropdown-menu'
import "./styles/style.css";

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics32 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']

  const ChartData: any = {
    series: [
      {
        name: 'Profit',
        data: [60, 40, 37, 35, 35, 20, 30, 20],
      },
      {
        name: 'Expenses',
        data: [15, 30, 15, 35, 25, 30, 30, 40],
      },
    ],
    labels: ['2024', '2023', '2022'],
    chart: {
      height: 300,
      stacked: true,
      type: 'bar',
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
      toolbar: {
        show: false,
      },
    },
    colors: ['var(--color-blue-500)', 'var(--color-red-500)'],
    plotOptions: {
      bar: {
        horizontal: false,
        columnWidth: '27%',
        borderRadius: 6,
      },
    },
    dataLabels: {
      enabled: false,
    },
    stroke: {
      show: false,
    },

    legend: {
      show: false,
    },
    grid: {
      borderColor: 'var(--color-muted)',
      padding: { top: 0, bottom: -8, left: 20, right: 20 },
    },
    xaxis: {
      categories: ['Aug', 'Sep', 'Oct', 'Nov', 'Dec', 'Jan', 'Feb', 'March'],
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      min: 0,
      max: 80,
      tickAmount: 4, // (80 - 0) / 20 = 4 intervals
      labels: {
        formatter: function (val: number) {
          return val.toFixed(0)
        },
      },
    },
    fill: {
      opacity: 1,
    },

    tooltip: {
      theme: 'dark',
      fillSeriesColor: false,
      y: {
        formatter: function (value: number) {
          return `$${value.toLocaleString()}K`
        },
      },
    },
  }

  const stats = [
    {
      title: '$63,489.50',
      subtitle: 'Profit',
      color: 'bg-blue-500/10 text-blue-500',
      profit: false,
      icon: 'solar:graph-new-up-bold',
    },
    {
      title: '$48,820.00',
      subtitle: 'Expenses',
      color: 'bg-red-500/10 text-red-500',
      profit: true,
      icon: 'solar:pie-chart-2-bold',
    },
    {
      title: '$103,582.50',
      subtitle: 'Overall earnings',
      color: 'bg-teal-400/10 text-teal-400',
      profit: false,
      icon: 'solar:archive-up-minimlistic-bold',
    },
  ]
  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-3xl p-6'>
          <div className='flex justify-between align-baseline'>
            <h5 className='text-lg font-semibold text-foreground'>Profit & Expenses</h5>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent hover:text-accent-foreground cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <div className='grid grid-cols-12 gap-6'>
            <div className='md:col-span-8 col-span-12'>
              <div className='mt-3 rounded-bars -ms-3 h-[300px]'>
                <Chart
                  options={ChartData}
                  series={ChartData.series}
                  type='bar'
                  height='300px'
                  width='100%'
                />
              </div>
            </div>
            <div className='md:col-span-4 col-span-12 pt-8 pe-4'>
              <div className='flex flex-col gap-7'>
                {stats.map((item, index) => (
                  <div
                    className='flex items-center justify-between'
                    key={index}>
                    <div className='items-center gap-4'>
                      <p className='text-sm font-medium text-muted-foreground'>{item.subtitle}</p>
                      <h5 className='text-base font-semibold text-foreground flex gap-2 items-baseline'>
                        {item.title}{' '}
                      </h5>
                    </div>

                    <div
                      className={`h-10 w-10 rounded-full flex justify-center items-center ${item.color}`}>
                      <Icon
                        icon={item.icon}
                        height='22'
                      />
                    </div>
                  </div>
                ))}
                <Button className='bg-blue-500 hover:bg-blue-500/90 text-white rounded-full cursor-pointer'>View Full Report</Button>
              </div>
            </div>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics32
