import Statistics33 from "@/components/shadcn-space/blocks/statistics-33/statistics33"

export default function Page() {
    return (
        <div>
            <Statistics33 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuItem,
  DropdownMenuContent,
} from '@/components/ui/dropdown-menu'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics33 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']

  const ChartData: any = {
    series: [
      {
        colors: ['var(--color-blue-500)'],
        name: 'Product Sales',
        data: [13, 15, 14, 17, 16, 19, 17],
      },
    ],

    chart: {
      height: 240,
      type: 'area',
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
      toolbar: {
        show: false,
      },
    },
    fill: {
      type: 'gradient',
      gradient: {
        shadeIntensity: 0,
        inverseColors: false,
        opacityFrom: 0.2,
        opacityTo: 0,
        stops: [20, 180],
      },
    },
    dataLabels: {
      enabled: false,
    },

    grid: {
      borderColor: 'var(--color-muted)',
      strokeDashArray: 4,
      strokeWidth: 1,
      padding: {
        top: 0,
        right: 0,
        bottom: 0,
        left: 5,
      },
    },
    stroke: {
      curve: 'smooth',
      width: '2',
    },
    xaxis: {
      categories: ['2016', '2017', '2018', '2019', '2020', '2021', '2022'],
      axisBorder: {
        show: false,
      },
      axisTicks: {
        show: false,
      },
    },
    yaxis: {
      labels: {
        show: false,
      },
    },

    tooltip: {
      theme: 'dark',
      fillSeriesColor: false,
    },
  }
  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-sm p-6'>
          <div className='flex justify-between align-baseline'>
            <h5 className='text-lg font-semibold text-foreground'>
              Product Sales
            </h5>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent hover:text-accent-foreground cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>
          <div className='my-3 h-[240px]'>
            <Chart
              options={ChartData}
              series={ChartData.series}
              type='area'
              height='240px'
              width='100%'
            />
          </div>

          <div className='flex gap-4'>
            <div
              className={`h-10 w-10 rounded-full flex justify-center items-center bg-blue-500/10`}>
              <Icon
                icon='solar:user-circle-outline'
                width={25}
                height={25}
                className='text-blue-500'
              />
            </div>
            <div>
              <h5 className='text-base font-semibold text-foreground flex gap-2'>
                36,436{' '}
                <Badge className='bg-teal-400/10 text-teal-400 border-teal-400'>
                  +12%
                </Badge>
              </h5>
              <p className='text-sm font-normal text-muted-foreground'>
                New Customer
              </p>
            </div>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics33
