import Statistics34 from "@/components/shadcn-space/blocks/statistics-34/statistics34"

export default function Page() {
    return (
        <div>
            <Statistics34 />
        </div>
    )
}

'use client'

import dynamic from 'next/dynamic'
import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Card } from '@/components/ui/card'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuItem,
  DropdownMenuContent,
} from '@/components/ui/dropdown-menu'

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false })

const Statistics34 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']

  const ChartData: any = {
    series: [5368, 3319, 3500, 4106],
    labels: ['Others', 'Direct Traffic', 'Refferal Traffic', 'Oragnic Traffic'],
    chart: {
      height: 270,
      type: 'donut',
      fontFamily: 'inherit',
      foreColor: '#adb0bb',
      toolbar: {
        show: false,
      },
    },
    colors: [
      'var(--color-neutral-200)',
      'var(--color-amber-300)',
      'var(--color-orange-400)',
      'var(--color-blue-500)',
    ],
    plotOptions: {
      pie: {
        donut: {
          size: '75%',
          background: 'none',
          labels: {
            show: true,
            name: {
              show: true,
              fontSize: '18px',
              color: undefined,
              offsetY: 5,
            },
            value: {
              show: false,
              color: '#98aab4',
            },
          },
        },
      },
    },
    dataLabels: {
      enabled: false,
    },
    stroke: {
      show: false,
    },
    legend: {
      show: false,
    },

    tooltip: {
      theme: 'dark',
      fillSeriesColor: false,
    },
  }

  const stats = [
    {
      title: '4,106',
      subtitle: 'Oragnic Traffic',
      color: 'text-blue-500',
      profit: true,
    },
    {
      title: '3,500',
      subtitle: 'Refferal Traffic',
      color: 'text-orange-400',
      profit: false,
    },
    {
      title: '3,319',
      subtitle: 'Direct Traffic',
      color: 'text-amber-300',
      profit: false,
    },
    {
      title: '5,368',
      subtitle: 'Other',
      color: 'text-[#E7ECF0]',
      profit: false,
    },
  ]
  return (
    <>
      <div className='min-h-screen flex flex-col items-center justify-center'>
        <Card className='w-full max-w-xl p-6'>
          <div className='flex justify-between align-baseline'>
            <h5 className='text-lg font-semibold text-foreground'>
              Traffic Distribution
            </h5>
            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent hover:text-accent-foreground cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>
          <div className='flex md:flex-row flex-col gap-6 md:items-center mt-6'>
            <div>
              <Chart
                options={ChartData}
                series={ChartData.series}
                type='donut'
                height='270px'
                width='100%'
              />
            </div>
            <div>
              <div className='flex flex-col gap-6'>
                {stats.map((item, index) => (
                  <div className='flex items-start gap-2' key={index}>
                    {/* Icon */}
                    <div className='mt-1'>
                      <Icon
                        icon='tabler:circle'
                        width={16}
                        height={16}
                        className={item.color}
                      />
                    </div>

                    {/* Text */}
                    <div>
                      <h6 className='text-base font-semibold text-foreground flex items-center gap-1'>
                        {item.title}
                        {item.profit && (
                          <span className='text-xs text-teal-400'>+23%</span>
                        )}
                      </h6>

                      <p className='text-sm font-medium text-muted-foreground'>
                        {item.subtitle}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </Card>
      </div>
    </>
  )
}

export default Statistics34
