import Statistics36 from "@/components/shadcn-space/blocks/statistics-36/statistics36"

export default function Page() {
    return (
        <div>
            <Statistics36 />
        </div>
    )
}

'use client'

import { useCallback, useEffect, useRef, useState } from 'react'
import {
    animate,
    AnimatePresence,
    motion,
    useInView,
    useMotionValue,
    useTransform,
} from 'motion/react'
import { cn } from '@/lib/utils'
import { Card } from '@/components/ui/card'

// Animated flip words
const FlipWords = ({
    words,
    duration = 3000,
    className,
}: {
    words: string[]
    duration?: number
    className?: string
}) => {
    const [index, setIndex] = useState(0)

    const startNext = useCallback(() => {
        setIndex((prev) => (prev + 1) % words.length)
    }, [words.length])

    useEffect(() => {
        const timer = setTimeout(startNext, duration)
        return () => clearTimeout(timer)
    }, [index, duration, startNext])

    const currentWord = words[index]

    return (
        <AnimatePresence mode='popLayout'>
            <motion.div
                key={currentWord}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{
                    opacity: 0,
                    y: -40,
                    x: 40,
                    filter: 'blur(8px)',
                    scale: 2,
                    position: 'absolute',
                }}
                transition={{ type: 'spring', stiffness: 100, damping: 10 }}
                className={cn(
                    'z-10 inline-block relative text-left text-white px-2',
                    className
                )}>
                {currentWord.split(' ').map((word, i) => (
                    <motion.span
                        key={word + i}
                        initial={{ opacity: 0, y: 10, filter: 'blur(8px)' }}
                        animate={{ opacity: 1, y: 0, filter: 'blur(0px)' }}
                        transition={{ delay: i * 0.3, duration: 0.3 }}
                        className='inline-block whitespace-nowrap'>
                        {word.split('').map((letter, j) => (
                            <motion.span
                                key={letter + j}
                                initial={{ opacity: 0, y: 10, filter: 'blur(8px)' }}
                                animate={{ opacity: 1, y: 0, filter: 'blur(0px)' }}
                                transition={{ delay: i * 0.3 + j * 0.05, duration: 0.2 }}
                                className='inline-block'>
                                {letter}
                            </motion.span>
                        ))}
                        <span>&nbsp;</span>
                    </motion.span>
                ))}
            </motion.div>
        </AnimatePresence>
    )
}

// Animated numeric counter
function AnimatedCounter({
    value,
    prefix = '',
    suffix = '',
    isInView = true,
}: {
    value: number
    prefix?: string
    suffix?: string
    isInView?: boolean
}) {
    const count = useMotionValue(0)
    const rounded = useTransform(count, (latest) =>
        Math.round(latest).toLocaleString()
    )

    useEffect(() => {
        if (isInView) {
            const controls = animate(count, value, { duration: 1.5, ease: 'easeOut' })
            return controls.stop
        }
    }, [count, value, isInView])

    return (
        <motion.span>
            {prefix}
            <motion.span>{rounded}</motion.span>
            {suffix}
        </motion.span>
    )
}

const Statistics36 = () => {
    const ref = useRef(null)
    const isInView = useInView(ref, { once: true, amount: 0.2 })

    return (
        <div className='min-h-screen flex flex-col items-center justify-center'>
            <Card
                ref={ref}
                className='w-full max-w-xl bg-sky-400 dark:bg-sky-400 relative p-6'>
                <div className='sm:w-3/5 w-full flex flex-col sm:items-start items-center'>
                    <h4 className='text-white text-xl font-semibold'>
                        Welcome
                        <FlipWords words={['Jonathan Deo', 'Back!']} duration={2500} />
                    </h4>

                    <p className='text-sm text-white font-medium mt-1'>
                        Check all the statastics
                    </p>

                    <div className='flex align-center rounded-full justify-between bg-gray-800/10 w-fit mt-4'>
                        <div className='py-3 px-6 text-center'>
                            <h5 className='text-white text-lg font-semibold leading-[normal]'>
                                <AnimatedCounter value={573} isInView={isInView} />
                            </h5>
                            <small className='text-white text-xs font-medium block'>
                                New Leads
                            </small>
                        </div>

                        <div className='py-3 px-6 text-center border-s border-white/20'>
                            <h5 className='text-white text-lg font-semibold leading-[normal]'>
                                <AnimatedCounter value={87} suffix='%' isInView={isInView} />
                            </h5>
                            <small className='text-white text-xs font-medium block'>
                                Conversion
                            </small>
                        </div>
                    </div>
                </div>

                <img
                    src='https://images.shadcnspace.com/assets/backgrounds/make-social-media.png'
                    alt='background'
                    className='absolute 2xl:end-0 lg:-end-6 end-0 -top-7 sm:block hidden'
                    width={294}
                    height={206}
                />
            </Card>
        </div>
    )
}

export default Statistics36
