"use client"
import { useEffect, useState } from "react"
import { Button } from "@/components/ui/button"
import {
    Sheet,
    SheetContent,
    SheetTitle,
    SheetTrigger,
} from "@/components/ui/sheet"
import { Icon } from "@iconify/react"
import LanguageDropdown from "@/components/shadcn-space/blocks/topbar-03/dropdown-language"
import ProfileDropdown from "@/components/shadcn-space/blocks/topbar-03/dropdown-profile"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import Link from "next/link"
import { Accordion, AccordionTrigger, AccordionContent, AccordionItem } from "@/components/ui/accordion"
import { VisuallyHidden } from "@radix-ui/react-visually-hidden";
import NavData from "@/components/shadcn-space/blocks/topbar-03/data"
import { NavigationMenu, NavigationMenuContent, NavigationMenuItem, NavigationMenuLink, NavigationMenuList, NavigationMenuTrigger } from "@/components/ui/navigation-menu"


function Sidebar({ onLinkClick }: { onLinkClick?: () => void }) {
    return (
        <nav className="space-y-1 px-4">
            <Accordion className="w-full">
                {NavData.map((group) => {
                    const hasChildren = Array.isArray(group.items) && group.items.length > 0

                    // SINGLE LINK ITEM
                    if (!hasChildren && group.href) {
                        return (
                            <Link
                                key={group.label}
                                href={group.href}
                                onClick={onLinkClick}
                                className="flex items-center gap-2 rounded-md px-2 py-2 text-sm font-normal
                           hover:bg-accent hover:text-foreground"
                            >
                                <Icon icon={group.icon} width={16} height={16} />
                                <span>{group.label}</span>
                            </Link>
                        )
                    }

                    // ACCORDION GROUP
                    return (
                        <AccordionItem
                            key={group.label}
                            value={group.label}
                            className="border-none"
                        >
                            <AccordionTrigger
                                className="flex items-center gap-2 px-2 py-2 text-sm
                           hover:no-underline cursor-pointer"
                            >
                                <div className="flex items-center gap-2">
                                    <Icon icon={group.icon} width={16} height={16} />
                                    <span className="tracking-normal!">{group.label}</span>
                                </div>
                            </AccordionTrigger>

                            <AccordionContent className="pl-4">
                                <div className="space-y-1">
                                    {group.items?.map((item: any) => (
                                        <Link
                                            key={item.label}
                                            href={item.href}
                                            onClick={onLinkClick}
                                            className="flex items-center gap-2 rounded-md px-2 py-1.5 text-sm
                                 hover:bg-accent hover:text-foreground"
                                        >
                                            <Icon icon={item.icon} width={14} height={14} />
                                            <span>{item.label}</span>
                                        </Link>
                                    ))}
                                </div>
                            </AccordionContent>
                        </AccordionItem>
                    )
                })}
            </Accordion>
        </nav>
    )
}


function NavDropdown({
    label,
    icon,
    items,
}: {
    label: string
    icon: string
    items: { label: string; icon: string; href: string }[]
}) {
    return (
        <NavigationMenuItem>
            <NavigationMenuTrigger
                className="
          gap-2 rounded-lg border border-transparent
          hover:bg-accent
          data-[state=open]:border-muted
          data-[state=open]:bg-accent cursor-pointer
        "
            >
                <Icon icon={icon} width={16} height={16} />
                <span className="text-sm">{label}</span>
            </NavigationMenuTrigger>

            <NavigationMenuContent className="min-w-48 p-1">
                <ul className="space-y-1">
                    {items.map((item) => (
                        <li key={item.label}>
                            <NavigationMenuLink >
                                <Link
                                    href={item.href}
                                    className="flex items-left gap-2 rounded-md px-2 py-1.5 text-sm hover:bg-accent hover:text-foreground"
                                >
                                    <div className="flex items-center gap-2">
                                        <Icon icon={item.icon} width={16} height={16} />
                                        <span>{item.label}</span>
                                    </div>
                                </Link>
                            </NavigationMenuLink>
                        </li>
                    ))}
                </ul>
            </NavigationMenuContent>
        </NavigationMenuItem>
    )
}


function NavButton({
    label,
    icon,
}: {
    label: string
    icon: string
}) {
    return (
        <Button  variant="ghost" className="gap-2 rounded-lg">
            <Link href="#">
                <Icon icon={icon} width={16} height={16} />
                <span className="text-sm">{label}</span>
            </Link>
        </Button>
    )
}

/* -------------------------- PAGE --------------------------*/

export default function Page() {
    const [sheetOpen, setSheetOpen] = useState(false)
    const BREAKPOINT = 1024

    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth >= BREAKPOINT) {
                setSheetOpen(false)
            }
        }

        handleResize()
        window.addEventListener("resize", handleResize)

        return () => window.removeEventListener("resize", handleResize)
    }, [])
    return (
        <div className="min-h-screen bg-background">
            {/* HEADER */}
            <header className="sticky top-0 z-50 border-b bg-background">
                {/* TOP BAR */}
                <div className="flex items-center justify-between p-4">
                    {/* LEFT */}
                    <div className="flex items-center gap-3">
                        {/* Mobile Sidebar */}
                        <Sheet open={sheetOpen} onOpenChange={setSheetOpen}>
                            <SheetTrigger className="lg:hidden">
                                <Icon icon="solar:hamburger-menu-linear" width={20} height={20} className="cursor-pointer" />
                            </SheetTrigger>

                            <SheetContent side="left" className="overflow-auto w-75!">
                                <VisuallyHidden>
                                    <SheetTitle>customizer</SheetTitle>
                                </VisuallyHidden>
                                <Link href="#" className="p-4 sticky top-0 bg-background z-10">
                                    <img
                                        src="https://images.shadcnspace.com/assets/logo/shadcnspace.svg"
                                        alt="logo"
                                        className="dark:hidden w-[160px] h-10"
                                    />
                                    <img
                                        src="https://images.shadcnspace.com/assets/logo/shadcnspace-white.svg"
                                        alt="logo"
                                        className="hidden dark:block w-[160px] h-10"
                                    />
                                </Link>
                                <Sidebar onLinkClick={() => setSheetOpen(false)} />
                            </SheetContent>
                        </Sheet>

                        <Link href="#">
                            <img
                                src="https://images.shadcnspace.com/assets/logo/shadcnspace.svg"
                                alt="logo"
                                className="dark:hidden w-[160px] h-10"
                            />
                            <img
                                src="https://images.shadcnspace.com/assets/logo/shadcnspace-white.svg"
                                alt="logo"
                                className="hidden dark:block w-[160px] h-10"
                            />
                        </Link>
                    </div>

                    {/* RIGHT */}
                    <div className='flex items-center gap-2'>
                        <Icon icon="solar:moon-line-duotone" width={16} height={16} className='cursor-pointer' />
                        <div className='relative cursor-pointer'>
                            <Icon icon="solar:bell-bing-outline" width={16} height={16} className="ml-2" />
                            <div className='rounded-full absolute -right-1.5 -top-1.5 bg-red-400 text-[8px] px-1 py-0.4 flex justify-center items-center text-white'>2</div>
                        </div>
                        <LanguageDropdown
                            trigger={
                                <Button variant='ghost' size='icon' className='focus-visible:ring-0! focus-visible:shadow-none! rounded-full! hover:bg-accent/80! cursor-pointer'>
                                    <Icon icon="solar:global-linear" width={16} height={16} />
                                </Button>
                            }
                        />
                        <ProfileDropdown
                            trigger={
                                <Button variant='ghost' size='icon' className='size-7 rounded-full cursor-pointer'>
                                    <Avatar className='size-7 rounded-full'>
                                        <AvatarImage src='https://images.shadcnspace.com/assets/profiles/user-11.jpg' />
                                        <AvatarFallback>NJ</AvatarFallback>
                                    </Avatar>
                                </Button>
                            }
                        />
                    </div>
                </div>

                {/* NAVIGATION BAR */}
                <div className="hidden lg:flex items-center justify-between px-4 py-2 border-t border-border">
                    <NavigationMenu viewport={false}>
                        <NavigationMenuList className="space-x-0">
                            {NavData.map((item) => {
                                if (item.type === "dropdown") {
                                    return (
                                        <NavDropdown
                                            key={item.label}
                                            label={item.label}
                                            icon={item.icon}
                                            items={item.items}
                                        />
                                    )
                                }
                                return (
                                    <NavigationMenuItem key={item.label}>
                                        <NavButton label={item.label} icon={item.icon} />
                                    </NavigationMenuItem>
                                )
                            })}
                        </NavigationMenuList>
                    </NavigationMenu>
                    <Button className="cursor-pointer">Login</Button>
                </div>
            </header>

            {/* MAIN CONTENT */}
            <main className="mx-auto max-w-7xl p-4 md:p-6">
                <div className="h-[500px] rounded-xl border border-border overflow-hidden" >
                    <div className="flex flex-1 flex-col gap-4 p-4">
                        {Array.from({ length: 8 }).map((_, index) => (
                            <div
                                key={index}
                                className="skeleton bg-muted/50 aspect-video h-12 w-full rounded-lg"
                            />
                        ))}
                    </div>
                </div>
            </main>
        </div>
    )
}

'use client'

import { useState, type ReactNode } from 'react'
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuRadioGroup,
    DropdownMenuRadioItem,
    DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import { Icon } from '@iconify/react'

type Props = {
    trigger: ReactNode
    defaultOpen?: boolean
    align?: 'start' | 'center' | 'end'
}

type Language = {
    value: string
    label: string
    icon: string
}

const LANGUAGES: Language[] = [
    {
        value: 'english',
        label: 'English (UK)',
        icon: 'circle-flags:uk',
    },
    {
        value: 'chinese',
        label: '中国人 (Chinese)',
        icon: 'circle-flags:lang-zh',
    },
    {
        value: 'french',
        label: 'français (French)',
        icon: 'circle-flags:lang-fr',
    },
    {
        value: 'arabic',
        label: 'عربي (Arabic)',
        icon: 'circle-flags:lang-ar',
    },
]

const itemClass =
    'cursor-pointer gap-2 pl-2 text-sm data-[state=checked]:bg-accent data-[state=checked]:text-accent-foreground [&>span]:hidden'

const LanguageDropdown = ({ trigger, defaultOpen, align = 'end' }: Props) => {
    const [language, setLanguage] = useState(LANGUAGES[0].value)

    return (
        <DropdownMenu defaultOpen={defaultOpen}>
            <DropdownMenuTrigger >{trigger}</DropdownMenuTrigger>

            <DropdownMenuContent className="w-50" align={align}>
                <DropdownMenuRadioGroup
                    value={language}
                    onValueChange={setLanguage}
                    className="flex flex-col gap-2"
                >
                    {LANGUAGES.map(({ value, label, icon }) => (
                        <DropdownMenuRadioItem key={value} value={value} className={itemClass}>
                            <Icon icon={icon} width={16} height={16} />
                            {label}
                        </DropdownMenuRadioItem>
                    ))}
                </DropdownMenuRadioGroup>
            </DropdownMenuContent>
        </DropdownMenu>
    )
}

export default LanguageDropdown
