import Widget04 from "@/components/shadcn-space/blocks/widget-04/widget04"

export default function Page() {
    return (
        <div>
            <Widget04 />
        </div>
    )
}

'use client'

import React, { useState } from 'react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Icon } from '@iconify/react'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuItem,
  DropdownMenuContent,
} from '@/components/ui/dropdown-menu'
import { Badge } from '@/components/ui/badge'
import { Card } from '@/components/ui/card'
import { Tabs, TabsList, TabsTrigger, TabsContent } from '@/components/ui/tabs'
import SimpleBar from 'simplebar-react'
import 'simplebar-react/dist/simplebar.min.css'
import { motion } from 'motion/react'

const Widget04 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']
  const DeliveredData = [
    {
      icon: 'scooter-line-duotone',
      iconBg: 'bg-amber-300',
      title: 'Scooter Freight',
      code: '#1245-456201',
      statustext: 'Delivered',
      statuscolor: 'bg-teal-400/10 text-teal-400',
      timeline: [
        {
          id: 1,
          timelineIcon: 'soundwave-circle-bold-duotone',
          iconcolor: 'text-amber-300',
          title: 'Drainage',
          country: 'United Kingdom',
        },
        {
          id: 2,
          timelineIcon: 'soundwave-circle-bold-duotone',
          iconcolor: 'text-amber-300',
          title: 'The lowland zone',
          country: 'United Kingdom',
        },
      ],
    },
    {
      icon: 'bus-line-duotone',
      iconBg: 'bg-blue-500',
      title: 'Bus Freight',
      code: '#9002-125423',
      statustext: 'All',
      statuscolor: 'bg-teal-400/10 text-teal-400',
      timeline: [
        {
          id: 1,
          timelineIcon: 'soundwave-circle-bold-duotone',
          iconcolor: 'text-blue-500',
          title: 'Messina Harbor',
          country: 'Sicily, Italy',
        },
        {
          id: 2,
          timelineIcon: 'map-point-bold-duotone',
          iconcolor: 'text-blue-500',
          title: 'Hektor Container Hotel',
          country: 'Tallin, EST',
        },
      ],
    },
    {
      icon: 'tram-linear',
      iconBg: 'bg-sky-400',
      title: 'Train Freight',
      code: '#1245-780652',
      statustext: 'Delivered',
      statuscolor: 'bg-green-500/10 text-green-500',
      timeline: [
        {
          id: 1,
          timelineIcon: 'soundwave-circle-bold-duotone',
          iconcolor: 'text-sky-400',
          title: 'Messina Harbor',
          country: 'Lester, United Kingdom',
        },
        {
          id: 2,
          timelineIcon: 'map-point-bold-duotone',
          iconcolor: 'text-sky-400',
          title: 'Laxmi Empire Hotel',
          country: 'Mumbai, India',
        },
      ],
    },
  ]
  const ShippingData = [
    {
      icon: 'tram-linear',
      iconBg: 'bg-sky-400',
      title: 'Train Freight',
      code: '#1245-780652',
      statustext: 'Shipping',
      statuscolor: 'bg-sky-400/10 text-sky-400',
      timeline: [
        {
          id: 1,
          timelineIcon: 'soundwave-circle-bold-duotone',
          iconcolor: 'text-sky-400',
          title: 'Messina Harbor',
          country: 'Lester, United Kingdom',
        },
        {
          id: 2,
          timelineIcon: 'map-point-bold-duotone',
          iconcolor: 'text-sky-400',
          title: 'Laxmi Empire Hotel',
          country: 'Mumbai, India',
        },
      ],
    },
    {
      icon: 'scooter-line-duotone',
      iconBg: 'bg-amber-300',
      title: 'Scooter Freight',
      code: '#1245-456201',
      statustext: 'Shipping',
      statuscolor: 'bg-blue-500/10 text-blue-500',
      timeline: [
        {
          id: 1,
          timelineIcon: 'soundwave-circle-bold-duotone',
          iconcolor: 'text-amber-300',
          title: 'Drainage',
          country: 'United Kingdom',
        },
        {
          id: 2,
          timelineIcon: 'soundwave-circle-bold-duotone',
          iconcolor: 'text-amber-300',
          title: 'The lowland zone',
          country: 'United Kingdom',
        },
      ],
    },
    {
      icon: 'bus-line-duotone',
      iconBg: 'bg-blue-500',
      title: 'Bus Freight',
      code: '#9002-125423',
      statustext: 'Shipping',
      statuscolor: 'bg-blue-500/10 text-blue-500',
      timeline: [
        {
          id: 1,
          timelineIcon: 'soundwave-circle-bold-duotone',
          iconcolor: 'text-blue-500',
          title: 'Messina Harbor',
          country: 'Sicily, Italy',
        },
        {
          id: 2,
          timelineIcon: 'map-point-bold-duotone',
          iconcolor: 'text-blue-500',
          title: 'Hektor Container Hotel',
          country: 'Tallin, EST',
        },
      ],
    },
  ]

  // Custom Tab
  const [activeTab, setActiveTab] = useState('Delivered')
  const handleTabClick = (tab: React.SetStateAction<string>) => {
    setActiveTab(tab)
  }

  return (
    <>
      <div className='min-h-screen flex items-center justify-center px-6'>
        <Card className='w-full max-w-sm gap-5'>
          <div className='px-6 flex items-center justify-between'>
            <div>
              <h5 className='text-lg font-semibold text-foreground'>
                Upcoming Schedules
              </h5>
              <p className='text-sm font-normal text-muted-foreground'>
                Our corporate events
              </p>
            </div>

            <div>
              <DropdownMenu>
                <DropdownMenuTrigger className='outline-none'>
                  <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent hover:text-accent-foreground cursor-pointer'>
                    <HiOutlineDotsVertical size={22} />
                  </span>
                </DropdownMenuTrigger>
                <DropdownMenuContent>
                  {dropdownItems.map((items, index) => {
                    return (
                      <DropdownMenuItem
                        key={index}
                        className='font-normal cursor-pointer'>
                        {items}
                      </DropdownMenuItem>
                    )
                  })}
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>

          <Tabs
            defaultValue='Delivered'
            className='w-full'
            onValueChange={handleTabClick}
            value={activeTab}>
            <div className='px-6'>
              <TabsList className='flex w-full bg-muted p-1 rounded-full relative'>
                <TabsTrigger
                  value='Delivered'
                  className='min-w-[50%] py-2 px-4 rounded-full text-sm font-semibold
          data-[state=active]:bg-white data-[state=active]:dark:bg-darkgray 
          data-[state=active]:text-dark data-[state=active]:dark:text-black
          dark:text-white opacity-60 data-[state=active]:opacity-100 cursor-pointer
          transition-all duration-300 ease-in-out
          hover:opacity-80 data-[state=active]:hover:opacity-100
          relative z-10'
                  >
                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    transition={{
                      type: 'spring',
                      stiffness: 400,
                      damping: 17,
                    }}>
                    Delivered
                  </motion.button>
                </TabsTrigger>

                <TabsTrigger
                  value='Shipping'
                  className='min-w-[50%] py-2 px-4 rounded-full text-sm font-semibold
          data-[state=active]:bg-white data-[state=active]:dark:bg-darkgray 
          data-[state=active]:text-dark data-[state=active]:dark:text-black
          dark:text-white opacity-60 data-[state=active]:opacity-100 cursor-pointer
          transition-all duration-300 ease-in-out
          hover:opacity-80 data-[state=active]:hover:opacity-100
          relative z-10'
                  >
                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    transition={{
                      type: 'spring',
                      stiffness: 400,
                      damping: 17,
                    }}>
                    Shipping
                  </motion.button>
                </TabsTrigger>

                {/* Animated sliding indicator */}
                <motion.div
                  className='absolute top-1 bottom-1 bg-white dark:bg-darkgray rounded-full z-0'
                  initial={false}
                  animate={{
                    left: activeTab === 'Delivered' ? '0.25rem' : '50%',
                    width: 'calc(50% - 0.5rem)',
                  }}
                  transition={{
                    type: 'spring',
                    stiffness: 300,
                    damping: 30,
                  }}
                />
              </TabsList>
            </div>

            {/* Delivered */}
            <TabsContent value='Delivered' >
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.3 }}>
                <SimpleBar className='max-h-[378px]'>
                  <motion.div
                    className='timeline-tabs px-6'
                    initial='hidden'
                    animate='visible'
                    variants={{
                      visible: {
                        transition: {
                          staggerChildren: 0.1,
                        },
                      },
                    }}>
                    {DeliveredData.map((item, index) => (
                      <motion.div
                        className='mt-6 timeline-list border-b pb-2 last:border-b-0'
                        key={index}
                        variants={{
                          hidden: { opacity: 0, x: -20 },
                          visible: { opacity: 1, x: 0 },
                        }}
                        transition={{ duration: 0.4 }}>
                        <motion.div
                          className='flex justify-between items-center'
                          initial={{ opacity: 0 }}
                          animate={{ opacity: 1 }}
                          transition={{ delay: index * 0.1 }}>
                          <div className='flex gap-3 items-center'>
                            <motion.div
                              className={`h-10 w-10 flex justify-center items-center rounded-md ${item.iconBg}`}
                              whileHover={{ scale: 1.1, rotate: 5 }}
                              transition={{ type: 'spring', stiffness: 300 }}>
                              <Icon
                                icon={`solar:${item.icon}`}
                                className='text-white'
                                height='25'
                              />
                            </motion.div>
                            <div>
                              <p className='text-sm font-medium text-muted-foreground'>
                                {item.title}
                              </p>
                              <h6 className='text-sm font-semibold text-foreground'>
                                {item.code}
                              </h6>
                            </div>
                          </div>
                          <Badge className={item.statuscolor}>
                            {item.statustext}
                          </Badge>
                        </motion.div>

                        {item.timeline.map((time, i) => (
                          <motion.div
                            className='mt-5 timeline'
                            key={i}
                            initial={{ opacity: 0, x: -10 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ delay: index * 0.1 + i * 0.05 }}>
                            <div className='flex gap-3 items-center mb-4'>
                              {/* ICON + LINE */}
                              <div
                                className={`
          relative flex flex-col items-center          
          after:content-['']
          after:absolute
          after:top-[40px]
          after:h-[20px]
          after:w-px
          after:bg-[var(--color-border)]
          after:left-1/2
          after:-translate-x-1/2

          ${i === item.timeline.length - 1 ? 'after:hidden' : ''}
        `}>
                                <motion.div
                                  className='h-10 w-10 border flex justify-center items-center rounded-full'
                                  whileHover={{ scale: 1.15 }}
                                  transition={{
                                    type: 'spring',
                                    stiffness: 400,
                                  }}>
                                  <Icon
                                    icon={`solar:${time.timelineIcon}`}
                                    className={time.iconcolor}
                                    height='25'
                                  />
                                </motion.div>
                              </div>

                              {/* TEXT */}
                              <div>
                                <p className='text-sm font-medium text-muted-foreground'>
                                  {time.title}
                                </p>
                                <h6 className='text-sm font-semibold text-foreground'>
                                  {time.country}
                                </h6>
                              </div>
                            </div>
                          </motion.div>
                        ))}
                      </motion.div>
                    ))}
                  </motion.div>
                </SimpleBar>
              </motion.div>
            </TabsContent>

            {/* Shipping */}
            <TabsContent value='Shipping' >
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.3 }}>
                <SimpleBar className='max-h-[378px]'>
                  <motion.div
                    className='timeline-tabs px-6'
                    initial='hidden'
                    animate='visible'
                    variants={{
                      visible: {
                        transition: {
                          staggerChildren: 0.1,
                        },
                      },
                    }}>
                    {ShippingData.map((item, index) => (
                      <motion.div
                        className='mt-6 timeline-list border-b pb-2 last:border-b-0'
                        key={index}
                        variants={{
                          hidden: { opacity: 0, x: -20 },
                          visible: { opacity: 1, x: 0 },
                        }}
                        transition={{ duration: 0.4 }}>
                        <motion.div
                          className='flex justify-between items-center'
                          initial={{ opacity: 0 }}
                          animate={{ opacity: 1 }}
                          transition={{ delay: index * 0.1 }}>
                          <div className='flex gap-3 items-center'>
                            <motion.div
                              className={`h-10 w-10 flex justify-center items-center rounded-md ${item.iconBg}`}
                              whileHover={{ scale: 1.1, rotate: 5 }}
                              transition={{ type: 'spring', stiffness: 300 }}>
                              <Icon
                                icon={`solar:${item.icon}`}
                                className='text-white'
                                height='25'
                              />
                            </motion.div>
                            <div>
                              <p className='text-sm font-medium text-muted-foreground'>
                                {item.title}
                              </p>
                              <h6 className='text-sm font-semibold text-foreground'>
                                {item.code}
                              </h6>
                            </div>
                          </div>
                          <Badge className={item.statuscolor}>
                            {item.statustext}
                          </Badge>
                        </motion.div>

                        {item.timeline.map((time, i) => (
                          <motion.div
                            className='mt-5 timeline'
                            key={i}
                            initial={{ opacity: 0, x: -10 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ delay: index * 0.1 + i * 0.05 }}>
                            <div className='flex gap-3 items-center mb-4'>
                              {/* ICON + LINE */}
                              <div
                                className={`
          relative flex flex-col items-center          
          after:content-['']
          after:absolute
          after:top-[40px]
          after:h-[20px]
          after:w-px
          after:bg-[var(--color-border)]
          after:left-1/2
          after:-translate-x-1/2

          ${i === item.timeline.length - 1 ? 'after:hidden' : ''}
        `}>
                                <motion.div
                                  className='h-10 w-10 border flex justify-center items-center rounded-full'
                                  whileHover={{ scale: 1.15 }}
                                  transition={{
                                    type: 'spring',
                                    stiffness: 400,
                                  }}>
                                  <Icon
                                    icon={`solar:${time.timelineIcon}`}
                                    className={time.iconcolor}
                                    height='25'
                                  />
                                </motion.div>
                              </div>

                              {/* TEXT */}
                              <div>
                                <p className='text-sm font-medium text-muted-foreground'>
                                  {time.title}
                                </p>
                                <h6 className='text-sm font-semibold text-foreground'>
                                  {time.country}
                                </h6>
                              </div>
                            </div>
                          </motion.div>
                        ))}
                      </motion.div>
                    ))}
                  </motion.div>
                </SimpleBar>
              </motion.div>
            </TabsContent>
          </Tabs>
        </Card>
      </div>
    </>
  )
}

export default Widget04
