import Widget06 from "@/components/shadcn-space/blocks/widget-06/widget06"

export default function Page() {
    return (
        <div>
            <Widget06 />
        </div>
    )
}

'use client'

import { useRef } from 'react'
import Link from 'next/link'
import { Icon } from '@iconify/react'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuItem,
  DropdownMenuContent,
} from '@/components/ui/dropdown-menu'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { motion, useInView } from 'motion/react'

const Widget06 = () => {
  const ref = useRef(null)
  const isInView = useInView(ref, { once: true, amount: 0.2 })

  const dropdownItems = ['Action', 'Another action', 'Something else']
  const EarningReportsData = [
    {
      icon: 'solar:card-line-duotone',
      title: 'Bank Transfer',
      subtitle: 'and +1 more',
      iconcolor: 'bg-blue-500/10 text-blue-500',
      statuscolor: 'bg-blue-500/10 text-blue-500',
      statustext: '16.3%',
    },
    {
      icon: 'solar:wallet-2-line-duotone',
      title: 'Net Profit',
      subtitle: 'and +4 more',
      iconcolor: 'bg-red-500/10 text-red-500',
      statuscolor: 'bg-red-500/10 text-red-500',
      statustext: '12.55%',
    },
    {
      icon: 'solar:course-up-line-duotone',
      title: 'Total Income',
      subtitle: 'and +4 more',
      iconcolor: 'bg-sky-400/10 text-sky-400',
      statuscolor: 'bg-sky-400/10 text-sky-400',
      statustext: '12.55%',
    },
    {
      icon: 'solar:waterdrops-line-duotone',
      title: 'Total Expenses',
      subtitle: 'and +2 more',
      iconcolor: 'bg-amber-300/10 text-amber-300',
      statuscolor: 'bg-teal-400/10 text-teal-400',
      statustext: '8.28%',
    },
  ]
  return (
    <>
      <div className='min-h-screen flex items-center justify-center px-6'>
        <Card className='w-full max-w-sm gap-5 p-6'>
          <div className='flex items-center justify-between'>
            <h5 className='text-lg font-semibold text-foreground'>
              Earning Report
            </h5>
            <DropdownMenu>
              <DropdownMenuTrigger className='outline-none'>
                <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent hover:text-accent-foreground cursor-pointer'>
                  <HiOutlineDotsVertical size={22} />
                </span>
              </DropdownMenuTrigger>
              <DropdownMenuContent>
                {dropdownItems.map((items, index) => {
                  return (
                    <DropdownMenuItem key={index} className='font-normal cursor-pointer'>
                      {items}
                    </DropdownMenuItem>
                  )
                })}
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
          <motion.div
            ref={ref}
            className='flex flex-col'
            initial='hidden'
            animate={isInView ? 'visible' : 'hidden'}
            variants={{
              visible: {
                transition: {
                  staggerChildren: 0.1,
                  delayChildren: 0.2,
                },
              },
            }}>
            {EarningReportsData.map((item, index) => (
              <motion.div
                className='flex items-center justify-between py-5 first:pt-0 border-b border-border'
                key={index}
                variants={{
                  hidden: { opacity: 0, y: 20 },
                  visible: { opacity: 1, y: 0 },
                }}
                transition={{
                  type: 'spring',
                  stiffness: 300,
                  damping: 24,
                }}
                whileHover={{ scale: 1.02, y: -2 }}
                whileTap={{ scale: 0.98 }}>
                <div className='flex gap-3 items-center'>
                  <span
                    className={`w-10 h-10 rounded-full flex items-center justify-center ${item.iconcolor}`}>
                    <Icon icon={item.icon} height={24} />
                  </span>
                  <div>
                    <h4 className='text-base font-semibold text-foreground'>
                      {item.title}
                    </h4>
                    <p className='text-xs font-normal text-muted-foreground flex items-center gap-1'>
                      {item.subtitle}{' '}
                      <Icon
                        icon='material-symbols:info-outline-rounded'
                        height={13}></Icon>
                    </p>
                  </div>
                </div>
                <Badge className={`${item.statuscolor}`}>
                  <Icon
                    icon='solar:alt-arrow-up-bold'
                    className='me-1'
                    height={12}
                  />
                  {item.statustext}
                </Badge>
              </motion.div>
            ))}
          </motion.div>
          <Link
            href={'/'}
            className='text-base  text-center font-semibold text-foreground hover:text-sky-400 dark:hover:text-sky-400 w-fit mx-auto'>
            View more markets
          </Link>
        </Card>
      </div>
    </>
  )
}

export default Widget06
