import Widget08 from "@/components/shadcn-space/blocks/widget-08/widget08"

export default function Page() {
    return (
        <div>
            <Widget08 />
        </div>
    )
}

"use client";
import { Card } from "@/components/ui/card";
import Link from "next/link";
import { motion } from "motion/react";

const RecentTransactionData = [
    {
        title: "09:30 ",
        subtitle: "Payment received from John Doe of $385.90",
        textcolor: "bg-blue-500",
        boldtext: false,
        line: true,
        link: "",
        url: "",
    },
    {
        title: "10:00 ",
        subtitle: "New sale recorded",
        textcolor: "bg-amber-300",
        boldtext: true,
        line: true,
        link: "#ML-3467",
        url: "",
    },
    {
        title: "12:00 ",
        subtitle: "Payment was made of $64.95 to Michael",
        textcolor: "bg-orange-400",
        boldtext: false,
        line: true,
        link: "",
        url: "",
    },
    {
        title: "09:30 ",
        subtitle: "New sale recorded",
        textcolor: "bg-amber-300",
        boldtext: true,
        line: true,
        link: "#ML-3467",
        url: "",
    },
    {
        title: "09:30 ",
        subtitle: "Project meeting",
        textcolor: "bg-blue-500",
        boldtext: true,
        line: true,
        link: "",
        url: "",
    },
    {
        title: "12:00 ",
        subtitle: "Payment received from John Doe of $385.90",
        textcolor: "bg-teal-400",
        boldtext: false,
        line: false,
        link: "",
        url: "",
    },
];

const Widget08 = () => {
    return (
        <div className='min-h-screen flex items-center justify-center px-6'>
            <Card className="p-7 w-full max-w-sm gap-5 border-0">
                <h5 className="text-lg font-semibold text-foreground">
                    Daily activities
                </h5>

                {/* Main Motion Wrapper */}
                <motion.div
                    className="mt-4"
                    initial="hidden"
                    animate="visible"
                    variants={{
                        visible: {
                            transition: { staggerChildren: 0.12 },
                        },
                    }}
                >
                    {RecentTransactionData.map((item, i) => (
                        <motion.div
                            key={i}
                            className="flex gap-x-3"
                            variants={{
                                hidden: { opacity: 0, y: 20 },
                                visible: { opacity: 1, y: 0 },
                            }}
                            transition={{ duration: 0.35 }}
                        >
                            {/* Time */}
                            <div className="w-1/6 text-end">
                                <span className="text-sm font-medium text-muted-foreground">
                                    {item.title}
                                </span>
                            </div>

                            {/* Dot + line */}
                            <div className="relative">
                                <motion.div
                                    className="relative z-10 w-7 h-5 flex justify-center items-center"
                                    whileHover={{ scale: 1.15 }}
                                    transition={{ type: "spring", stiffness: 300 }}
                                >
                                    <div
                                        className={`h-3 w-3 rounded-full ${item.textcolor}`}
                                    ></div>
                                </motion.div>

                                {item.line ? (
                                    <div className="border-s border-ld h-full -mt-2 ms-3.5"></div>
                                ) : (
                                    <div className="border-0"></div>
                                )}
                            </div>

                            {/* Text Content */}
                            <div className="w-1/4 grow pt-0.5 pb-5">
                                {item.boldtext ? (
                                    <p className="text-sm font-semibold text-foreground">
                                        {item.subtitle}
                                    </p>
                                ) : (
                                    <p className="text-sm font-medium text-muted-foreground">
                                        {item.subtitle}
                                    </p>
                                )}

                                {item.link ? (
                                    <Link href={item.url} className="text-blue-500 text-sm">
                                        {item.link}
                                    </Link>
                                ) : null}
                            </div>
                        </motion.div>
                    ))}
                </motion.div>
            </Card>
        </div>
    );
};

export default Widget08;
