import Widget09 from "@/components/shadcn-space/blocks/widget-09/widget09"

export default function Page() {
    return (
        <div>
            <Widget09 />
        </div>
    )
}

'use client'

import { Card } from '@/components/ui/card'
import { motion } from 'motion/react'

const userImg = [
  {
    user: 'https://images.shadcnspace.com/assets/profiles/user-4.jpg',
  },
  {
    user: 'https://images.shadcnspace.com/assets/profiles/user-2.jpg',
  },
  {
    user: 'https://images.shadcnspace.com/assets/profiles/user-3.jpg',
  },
  {
    user: 'https://images.shadcnspace.com/assets/profiles/user-6.jpg',
  },
]

const Widget09 = () => {
  return (
    <div className='min-h-screen flex items-center justify-center px-6'>
      {/* Card Motion Wrapper */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, ease: 'easeOut' }}
        className='w-full max-w-sm'>
        <Card className='p-0 w-full gap-5 border-0 overflow-hidden'>
          {/* Top Image Animation */}
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5, delay: 0.1 }}>
            <img
              src={
                'https://images.shadcnspace.com/assets/backgrounds/figma-img.jpg'
              }
              alt='matdash'
              height={220}
              width={500}
              className='rounded-t-xl'
            />
          </motion.div>

          {/* Content Area */}
          <div className='p-6 pt-5'>
            {/* Title + Text fade stagger */}
            <motion.div
              initial='hidden'
              animate='visible'
              variants={{
                visible: { transition: { staggerChildren: 0.15 } },
              }}>
              <motion.h6
                variants={{
                  hidden: { opacity: 0, y: 20 },
                  visible: { opacity: 1, y: 0 },
                }}
                transition={{ duration: 0.4 }}
                className='text-lg font-semibold text-foreground'>
                Figma tips and tricks with Stephan
              </motion.h6>

              <motion.p
                variants={{
                  hidden: { opacity: 0, y: 20 },
                  visible: { opacity: 1, y: 0 },
                }}
                transition={{ duration: 0.45 }}
                className='text-sm font-medium text-muted-foreground mt-2'>
                Nullam lobortis sodales dolor vitae viverra.
                <br />
                Cras lacinia bibendum metus vel rhoncus.
              </motion.p>
            </motion.div>

            {/* Avatar Group Animation */}
            <motion.div
              className='flex mt-4'
              initial='hidden'
              animate='visible'
              variants={{
                visible: {
                  transition: { staggerChildren: 0.12 },
                },
              }}>
              {userImg.map((item, index) => (
                <motion.div
                  key={index}
                  variants={{
                    hidden: { opacity: 0, scale: 0.7 },
                    visible: { opacity: 1, scale: 1 },
                  }}
                  whileHover={{ scale: 1.08 }}
                  transition={{ type: 'spring', stiffness: 250 }}
                  className='-ms-2 h-11 w-11'>
                  <img
                    src={item.user}
                    className='border-2 border-border rounded-full'
                    alt='icon'
                    width={44}
                    height={44}
                  />
                </motion.div>
              ))}

              {/* +12 bubble animation */}
              <motion.div
                variants={{
                  hidden: { opacity: 0, scale: 0.7 },
                  visible: { opacity: 1, scale: 1 },
                }}
                whileHover={{ scale: 1.08 }}
                transition={{ type: 'spring', stiffness: 250 }}
                className='-ms-2'>
                <div className='bg-blue-500/10 border-2 border-border h-11 w-11 flex justify-center items-center text-blue-500 rounded-full dark:bg-lightprimary'>
                  +12
                </div>
              </motion.div>
            </motion.div>
          </div>
        </Card>
      </motion.div>
    </div>
  )
}

export default Widget09
