import Widget10 from "@/components/shadcn-space/blocks/widget-10/widget10"

export default function Page() {
    return (
        <div>
            <Widget10 />
        </div>
    )
}

"use client";
import { Icon } from "@iconify/react";
import { Button } from "@/components/ui/button";
import Link from "next/link";
import SimpleBar from "simplebar-react";
import { Card } from "@/components/ui/card";
import "./styles/style.css";
const ColorboxData = [
    {
        bg: "primary-gradient",
        icon: "solar:dollar-minimalistic-linear",
        color: "bg-blue-500",
        title: "Total Orders",
        price: "16,689",
        link: "#",
    },
    {
        bg: "warning-gradient",
        icon: "solar:recive-twice-square-linear",
        color: "bg-amber-300",
        title: "Return Item",
        price: "148",
        link: "#",
    },
    {
        bg: "secondary-gradient",
        icon: "ic:outline-backpack",
        color: "bg-teal-400",
        title: "Annual Budget",
        price: "$156K",
        link: "#",
    },
    {
        bg: "error-gradient",
        icon: "ic:baseline-sync-problem",
        color: "bg-red-500",
        title: "Cancel Orders",
        price: "64",
        link: "#",
    },
    {
        bg: "success-gradient",
        icon: "ic:outline-forest",
        color: "bg-sky-400",
        title: "Total Income ",
        price: "$36,715",
        link: "#",
    },
];

const Widget10 = () => {
    return (
        <div className='min-h-screen flex items-center justify-center px-6'>
            <Card className="p-7 w-full gap-5 border-0">
                <div className="overflow-x-auto">
                    <SimpleBar>
                        <div className="flex  gap-7">
                            {ColorboxData.map((item, index) => (
                                <div className="lg:basis-1/5 md:basis-1/4 basis-full lg:shrink shrink-0" key={index}>
                                    <div className={`text-center px-5 py-7 rounded-lg ${item.bg}`}>
                                        <span className={`h-12 w-12 mx-auto flex items-center justify-center rounded-lg ${item.color}`}>
                                            <Icon icon={item.icon}
                                                className="text-white"
                                                height={24}
                                            />
                                        </span>
                                        <p className="text-sm font-normal mt-4 mb-2">
                                            {item.title}
                                        </p>
                                        <h4 className="text-base! font-medium!">{item.price}</h4>
                                        <Button
                                            size="sm"
                                            className="w-fit mx-auto mt-5 bg-white hover:bg-neutral-800 text-ld font-semibold hover:text-white shadow-sm py-1 px-3 dark:bg-neutral-800 dark:hover:bg-neutral-700"
                                        >
                                            <Link href={item.link}>View Details</Link>
                                        </Button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </SimpleBar>
                </div>
            </Card>
        </div>
    );
};

export default Widget10;

.primary-gradient {
    background: linear-gradient(180deg, #3B82F61F 0%, #3B82F608 100%);
}

.warning-gradient {
    background: linear-gradient(180deg, #FCD34D1F 0%, #FCD34D08 100%);
}

.secondary-gradient {
    background: linear-gradient(180deg, #2DD4BF1F 0%, #2DD4BF08 100%);
}

.error-gradient {
    background: linear-gradient(180deg, #EF44441F 0%, #EF444408 100%);
}

.success-gradient {
    background: linear-gradient(180deg, #38BDF81F 0%, #38BDF808 100%);
}
