import Widget13 from "@/components/shadcn-space/blocks/widget-13/widget13"

export default function Page() {
    return (
        <div>
            <Widget13 />
        </div>
    )
}

'use client'

import { Icon } from '@iconify/react'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import { Card } from '@/components/ui/card'
import { Tabs, TabsList, TabsTrigger, TabsContent } from '@/components/ui/tabs'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'

// User images array
const users = [
  'https://images.shadcnspace.com/assets/profiles/user-2.jpg',
  'https://images.shadcnspace.com/assets/profiles/user-3.jpg',
  'https://images.shadcnspace.com/assets/profiles/user-4.jpg',
  'https://images.shadcnspace.com/assets/profiles/user-5.jpg',
]

// Reusable component for avatars + extra count
const UserAvatars = ({
  users,
  extraCount,
}: {
  users: string[]
  extraCount?: number
}) => (
  <div className='flex mt-4 -space-x-2'>
    {users.map((user, idx) => (
      <div className='h-8 w-8' key={idx}>
        <img
          src={user}
          className='border-2 border-border rounded-full'
          alt='user avatar'
        />
      </div>
    ))}
    {extraCount && (
      <div>
        <div className='bg-sky-400/10 text-sky-400 text-xs border-2 border-border h-8 w-8 flex justify-center items-center rounded-full'>
          +{extraCount}
        </div>
      </div>
    )}
  </div>
)

// Reusable Event Card
const EventCard = ({
  title,
  time,
  users,
  borderColor,
  extraCount,
}: {
  title: string
  time: string
  users: string[]
  borderColor: string
  extraCount?: number
}) => (
  <div className='mt-6'>
    <div className={`rounded-sm shadow-sm p-4 border-s-5 ${borderColor}`}>
      <h6 className='text-base font-semibold text-foreground'>{title}</h6>
      <p className='text-sm font-normal text-muted-foreground flex items-center gap-1'>
        <Icon icon='solar:clock-circle-linear' width={15} height={15} />
        {time}
      </p>
      <UserAvatars users={users} extraCount={extraCount} />
    </div>
  </div>
)

type TabKey = 'Morning' | 'Afternoon' | 'Evening'
const tabKeys: TabKey[] = ['Morning', 'Afternoon', 'Evening']

const scheduleData: Record<
  TabKey,
  { title: string; time: string; borderColor: string; extraCount: number }[]
> = {
  Morning: [
    {
      title: 'Marketing Meeting',
      time: '08:30 - 09:30',
      borderColor: 'border-blue-500',
      extraCount: 5,
    },
    {
      title: 'Cybersecurity Lecture',
      time: '09:45 - 11:00',
      borderColor: 'border-teal-400',
      extraCount: 12,
    },
  ],
  Afternoon: [
    {
      title: 'Team SEO Session',
      time: '12:00 - 13:00',
      borderColor: 'border-red-500',
      extraCount: 8,
    },
    {
      title: 'Client Meeting',
      time: '13:15 - 14:30',
      borderColor: 'border-blue-500',
      extraCount: 10,
    },
  ],
  Evening: [
    {
      title: 'Project Review',
      time: '15:00 - 16:30',
      borderColor: 'border-amber-300',
      extraCount: 7,
    },
    {
      title: 'Wrap-up & Planning',
      time: '16:45 - 17:30',
      borderColor: 'border-sky-400',
      extraCount: 6,
    },
  ],
}

// Helper function to generate dynamic times for a tab
const generateTimes = (events: { time: string }[]) => {
  const timesSet = new Set<string>()
  events.forEach((event) => {
    const [start, end] = event.time.split(' - ')
    let [currentHour, currentMinute] = start.split(':').map(Number)
    const [endHour, endMinute] = end.split(':').map(Number)

    while (
      currentHour < endHour ||
      (currentHour === endHour && currentMinute <= endMinute)
    ) {
      timesSet.add(
        `${String(currentHour).padStart(2, '0')}:${String(
          currentMinute
        ).padStart(2, '0')}`
      )
      currentMinute += 30
      if (currentMinute >= 60) {
        currentHour += 1
        currentMinute = 0
      }
    }
  })
  return Array.from(timesSet).sort()
}

const Widget13 = () => {
  const dropdownItems = ['Action', 'Another action', 'Something else']

  return (
    <div className='min-h-screen flex flex-col items-center justify-center'>
      <Card className='w-full max-w-xs relative p-6'>
        {/* Header with Dropdown */}
        <div className='flex justify-between items-center'>
          <h5 className='text-lg font-semibold text-foreground'>
            Upcoming Schedules
          </h5>
          <DropdownMenu>
            <DropdownMenuTrigger className='outline-none'>
              <span className='h-9 w-9 flex justify-center items-center rounded-full hover:bg-accent hover:text-accent-foreground cursor-pointer'>
                <HiOutlineDotsVertical size={22} />
              </span>
            </DropdownMenuTrigger>
            <DropdownMenuContent>
              {dropdownItems.map((item, i) => (
                <DropdownMenuItem
                  key={i}
                  className='font-normal cursor-pointer'>
                  {item}
                </DropdownMenuItem>
              ))}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        {/* Tabs */}
        <Tabs defaultValue='Morning'>
          <TabsList className='flex flex-wrap bg-muted rounded-full w-full'>
            {tabKeys.map((tab) => (
              <TabsTrigger
                key={tab}
                value={tab}
                className='py-2 px-4 rounded-full text-xs font-semibold data-[state=active]:bg-background dark:data-[state=active]:bg-dark data-[state=active]:text-foreground
                           data-[state=inactive]:text-muted-foreground cursor-pointer'>
                {tab}
              </TabsTrigger>
            ))}
          </TabsList>

          {/* Tab Contents */}
          {tabKeys.map((tab) => {
            const events = scheduleData[tab]
            const times = generateTimes(events)

            return (
              <TabsContent key={tab} value={tab}>
                <div className='grid grid-cols-12 gap-6 mt-2'>
                  {/* Time column */}
                  <div className='md:col-span-2 col-span-3'>
                    <ul className='flex flex-col gap-6'>
                      {times.map((time) => (
                        <li className='text-sm font-medium' key={time}>
                          {time}
                        </li>
                      ))}
                    </ul>
                  </div>

                  {/* Event Cards */}
                  <div className='md:col-span-10 col-span-9'>
                    {events.map((event, idx) => (
                      <EventCard key={idx} {...event} users={users} />
                    ))}
                  </div>
                </div>
              </TabsContent>
            )
          })}
        </Tabs>
      </Card>
    </div>
  )
}

export default Widget13
