import Widget15 from "@/components/shadcn-space/blocks/widget-15/widget15"

export default function Page() {
    return (
        <div>
            <Widget15 />
        </div>
    )
}

'use client'

import { useEffect, useState } from 'react'
import { Icon } from '@iconify/react'
import { Card } from '@/components/ui/card'
import { Button } from '@/components/ui/button'

const Widget15 = () => {
    const [greeting, setGreeting] = useState('')

    useEffect(() => {
        const hour = new Date().getHours()

        if (hour >= 5 && hour < 12) {
            setGreeting('Good Morning')
        } else if (hour >= 12 && hour < 17) {
            setGreeting('Good Afternoon')
        } else if (hour >= 17 && hour < 21) {
            setGreeting('Good Evening')
        } else {
            setGreeting('Good Night')
        }
    }, [])

    const getGreetingIcon = () => {
        if (greeting === 'Good Morning' || greeting === 'Good Afternoon') {
            return (
                <Icon
                    icon='line-md:sunny-filled-loop'
                    color='orange'
                    width='25'
                    height='25'
                />
            )
        } else {
            return (
                <Icon
                    icon='line-md:moon-filled-alt-loop'
                    color='white'
                    width='25'
                    height='25'
                />
            )
        }
    }

    return (
        <>
            <div className='min-h-screen flex flex-col items-center justify-center'>
                <Card className='w-full max-w-xl relative p-6'>
                    <div className='flex flex-col gap-8'>
                        <div>
                            <h5 className='text-lg font-semibold text-foreground flex items-center gap-2'>
                                {greeting}, Mike
                                {getGreetingIcon()}
                            </h5>
                            <p className='text-sm font-medium text-muted-foreground'>Welcome to shadcnspace!</p>
                        </div>
                        <div>
                            <Button className='w-fit rounded-full cursor-pointer'>Visit Now</Button>
                        </div>
                    </div>
                    <img
                        src='https://images.shadcnspace.com/assets/backgrounds/welcome-bg-3.png'
                        className='hidden sm:block sm:absolute -end-3 bottom-0 rtl:transform rtl:scale-x-[-1]'
                        alt='image'
                    />
                </Card>
            </div>
        </>
    )
}

export default Widget15
