import Widget16 from "@/components/shadcn-space/blocks/widget-16/widget16"

export default function Page() {
    return (
        <div>
            <Widget16 />
        </div>
    )
}

'use client'

import { Button } from '@/components/ui/button'
import { Card } from '@/components/ui/card'
import { Icon } from '@iconify/react/dist/iconify.js'
import Link from 'next/link'
import { useEffect, useState } from 'react'

const Widget16 = () => {
    const [greeting, setGreeting] = useState('')

    useEffect(() => {
        const hour = new Date().getHours()

        if (hour >= 5 && hour < 12) {
            setGreeting('Good Morning')
        } else if (hour >= 12 && hour < 17) {
            setGreeting('Good Afternoon')
        } else if (hour >= 17 && hour < 21) {
            setGreeting('Good Evening')
        } else {
            setGreeting('Good Night')
        }
    }, [])

    const getGreetingIcon = () => {
        if (greeting === 'Good Morning' || greeting === 'Good Afternoon') {
            return (
                <Icon
                    icon='line-md:sunny-filled-loop'
                    color='orange'
                    width='25'
                    height='25'
                />
            )
        } else {
            return (
                <Icon
                    icon='line-md:moon-filled-alt-loop'
                    color='white'
                    width='25'
                    height='25'
                />
            )
        }
    }

    return (
        <div className='min-h-screen flex flex-col items-center justify-center'>
            <Card className='bg-emerald-900 relative p-7 px-6 overflow-hidden h-full'>
                <div className='grid grid-cols-12 h-full'>
                    <div className='lg:col-span-7 col-span-12'>
                        <div className='flex flex-col gap-4'>
                            <div className='flex item-start gap-2'>
                                <h2 className='text-2xl! text-white tracking-wide!'>{greeting}, Cameron</h2>
                                <span className='flex items-center'>{getGreetingIcon()}</span>
                            </div>
                            <p className='text-sm font-normal text-white/60 max-w-88'>
                                Stay updated with your store's performance today. Get a quick
                                snapshot of key statistics.
                            </p>
                            <Link href={'/react-tables/order-datatable'}>
                                <Button className='flex gap-2 bg-lime-300 hover:bg-lime-300/80 text-neutral-900 cursor-pointer'>
                                    View Full Report
                                    <span>
                                        <Icon
                                            icon='solar:arrow-right-line-duotone'
                                            width={18}
                                            height={18}
                                        />
                                    </span>
                                </Button>
                            </Link>
                        </div>
                    </div>
                </div>
                <img
                    src="https://images.shadcnspace.com/assets/backgrounds/usergraph.webp"
                    width={190}
                    height={200}
                    alt='chasing_grades_bg'
                    className='absolute -bottom-2 end-8 lg:block hidden'
                />
            </Card>
        </div>
    )
}

export default Widget16
